// src/components/Login.js - FULL FIXED VERSION
import React, { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [typedText, setTypedText] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);
  const [showLoginModal, setShowLoginModal] = useState(true);
  const navigate = useNavigate();

  const phrases = useMemo(
    () => [
      "Generate AI Content",
      "Post to Multiple Platforms",
      "Smart Scheduling",
      "Boost Engagement",
      "Automate Marketing",
    ],
    []
  );

  const horizontalFeatures = [
    {
      icon: "ðŸ”—",
      title: "Multi-Platform Integration",
      description:
        "Connect to LinkedIn, Instagram, Facebook, Twitter, and more. Manage all your social media accounts from one dashboard.",
    },
    {
      icon: "ðŸ¤–",
      title: "AI Content Creation",
      description:
        "Automatically generate engaging posts, captions, and stunning images using advanced AI technology.",
    },
    {
      icon: "ðŸ“¤",
      title: "Auto Posting",
      description:
        "Schedule and publish content automatically across all your connected social media platforms with one click.",
    },
    {
      icon: "ðŸ“Š",
      title: "Advanced Analytics",
      description:
        "Track performance with detailed analytics, engagement metrics, and growth insights for all your platforms.",
    },
    {
      icon: "ðŸŽ¯",
      title: "Smart Scheduling",
      description:
        "Optimize posting times for maximum engagement using AI-powered scheduling algorithms.",
    },
    {
      icon: "âœ¨",
      title: "Premium Experience",
      description:
        "Enjoy a seamless, intuitive interface designed for both beginners and marketing professionals.",
    },
  ];

  // Auto-open login modal on component mount
  useEffect(() => {
    setShowLoginModal(true);
  }, []);

  // Typing effect
  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting
          ? fullText.substring(0, typedText.length - 1)
          : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 2000);
      } else if (isDeleting && typedText === "") {
        setIsDeleting(false);
        setLoopNum((n) => n + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  // Theme toggle
  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");

    if (newMode) {
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
    }
  };

  const openLoginModal = () => {
    setShowLoginModal(true);
    setError("");
    setSuccess("");
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);
    setError("");
    setSuccess("");
  };

  // Initial theme + remembered user + token validation
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark" || !saved) {
      setIsDarkMode(true);
      document.body.classList.add("dark");
    } else {
      setIsDarkMode(false);
      document.body.classList.remove("dark");
    }

    const rememberedUsername = localStorage.getItem("rememberedUsername");
    const rememberedRememberMe = localStorage.getItem("rememberMe") === "true";

    if (rememberedUsername && rememberedRememberMe) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }

    const token = localStorage.getItem("token");
    if (token) {
      const tokenExpiry = localStorage.getItem("tokenExpiry");
      if (tokenExpiry && parseInt(tokenExpiry, 10) > Date.now()) {
        navigate("/connect", { replace: true });
      } else {
        localStorage.removeItem("token");
        localStorage.removeItem("tokenExpiry");
      }
    }
  }, [navigate]);

  // Submit
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password, rememberMe }
      );

      const { token, user, rememberMe: rememberMeResponse, expiresIn } =
        response.data;

      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);

      const expiryTime = Date.now() + expiresIn * 1000;
      localStorage.setItem("tokenExpiry", expiryTime.toString());

      if (rememberMeResponse) {
        localStorage.setItem("rememberedUsername", username);
        localStorage.setItem("rememberMe", "true");
      } else {
        localStorage.removeItem("rememberedUsername");
        localStorage.removeItem("rememberMe");
      }

      setSuccess("Login successful! Redirecting...");

      if (onLogin) onLogin(user);

      setTimeout(() => {
        setIsLoading(false);
        closeLoginModal();
        navigate("/connect", { replace: true });
      }, 1000);
    } catch (err) {
      const errorMessage =
        err.response?.data?.error || "Invalid username or password";
      setError(errorMessage);
      setIsLoading(false);
    }
  };

  const socialPlatforms = [
    { name: "LinkedIn", icon: "/images/linkedin.png", color: "#0077B5" },
    { name: "Instagram", icon: "/images/instagram.jpg", color: "#E1306C" },
    { name: "Facebook", icon: "/images/facebook.png", color: "#1877F2" },
  ];

  const trustedCompanies = [
    { name: "Inikola", logo: "ðŸ¢", description: "Tech Innovation" },
    { name: "CraftingBrain", logo: "ðŸ§ ", description: "EdTech Company" },
  ];

  return (
    <div className="futuristic-login-container">
      <div className="cyber-grid"></div>

      <div className="geometric-bg">
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
      </div>

      <div className="floating-particles">
        {[...Array(20)].map((_, i) => (
          <div key={i} className="particle"></div>
        ))}
      </div>

      {/* Login Modal */}
      <div className={`login-modal-overlay ${showLoginModal ? "active" : ""}`}>
        <div className="login-modal">
          <button className="login-modal-close" onClick={closeLoginModal}>
            âœ•
          </button>

          <div className="form-header-futuristic">
            <h2>Welcome Back</h2>
            <p>Access your AI marketing dashboard</p>
          </div>

          <form className="futuristic-form" onSubmit={handleSubmit}>
            <div className="input-group-futuristic">
              <label>Username</label>
              <div className="input-wrapper">
                <input
                  type="text"
                  placeholder="Enter username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
                <div className="input-glow"></div>
              </div>
            </div>

            <div className="input-group-futuristic">
              <label>Password</label>
              <div className="input-wrapper password-wrapper-futuristic">
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
                <button
                  type="button"
                  className="password-toggle-futuristic"
                  onClick={() => setShowPassword((v) => !v)}
                  aria-label={showPassword ? "Hide password" : "Show password"}
                >
                  {showPassword ? "ðŸ‘ï¸" : "ðŸ‘ï¸â€ðŸ—¨ï¸"}
                </button>
                <div className="input-glow"></div>
              </div>
            </div>

            <div className="remember-me-futuristic">
              <label className="checkbox-container">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                />
                <span className="checkmark"></span>
                <span className="checkbox-label">Remember me for 30 days</span>
              </label>
            </div>

            {error && (
              <div className="alert-futuristic error-alert">
