// src/components/Login.js — Professional version with auto popup
import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Login.css";

export default function Login({ onLogin }) {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [remember, setRemember] = useState(true);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [open, setOpen] = useState(false); // modal open

  // Auto-open the login modal when page loads
  useEffect(() => {
    const t = setTimeout(() => setOpen(true), 100);
    return () => clearTimeout(t);
  }, []);

  const submit = async (e) => {
    e.preventDefault();
    setError("");
    setLoading(true);
    try {
      // TODO: replace with your real API call
      await new Promise((res) => setTimeout(res, 800));
      if (onLogin) onLogin({ username });
      navigate("/dashboard");
    } catch (err) {
      setError("Login failed. Please check your credentials.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="page">
      <header className="site-header">
        <div className="brand">
          <div className="logo">PE</div>
          <span className="brand-name">Posting Expert</span>
        </div>
        <nav className="nav">
          <Link to="/">Home</Link>
          <Link to="/about">About</Link>
          <Link to="/privacy">Privacy</Link>
          <Link to="/contact">Contact</Link>
          <Link to="/register" className="btn ghost">Create account</Link>
        </nav>
      </header>

      {/* Background hero */}
      <section className="hero">
        <div className="hero-content">
          <h1>Smart marketing, automated.</h1>
          <p>Let your AI assistant plan, create, and post across platforms.</p>
          <button className="btn primary" onClick={() => setOpen(true)}>Login</button>
        </div>
      </section>

      {/* Login Modal */}
      {open && (
        <div className="modal" role="dialog" aria-modal="true">
          <div className="modal-card">
            <div className="modal-header">
              <h2>Welcome back</h2>
              <button className="icon-btn" aria-label="Close" onClick={() => setOpen(false)}>×</button>
            </div>
            <form onSubmit={submit} className="form">
              {error && <div className="alert error">{error}</div>}

              <label className="label" htmlFor="username">Email or Username</label>
              <input
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                placeholder="you@example.com"
                className="input"
                autoFocus
              />

              <div className="row">
                <label className="label" htmlFor="password">Password</label>
                <Link to="/forgot" className="link subtle">Forgot?</Link>
              </div>
              <input
                id="password"
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="••••••••"
                className="input"
              />

              <label className="checkbox">
                <input type="checkbox" checked={remember} onChange={(e)=>setRemember(e.target.checked)} />
                <span>Remember me</span>
              </label>

              <button type="submit" className="btn primary wide" disabled={loading}>
                {loading ? "Signing in..." : "Sign in"}
              </button>

              <p className="muted">New here? <Link to="/register" className="link">Create an account</Link></p>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
