// src/components/ContentCreation.js ‚Äî Queue + Poll (CORS-safe, user_id in BODY)
import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './ContentCreation.css';

const API = 'http://13.233.45.167:5000';

const ContentCreation = ({ user }) => {
  const [prompt, setPrompt] = useState('');
  const [numImages, setNumImages] = useState('');
  const [contentType, setContentType] = useState('');
  const [platforms, setPlatforms] = useState({
    instagram: false,
    x: false,          // maps to twitter in payload
    linkedin: false,
    facebook: false,
  });

  const [errors, setErrors] = useState({});
  const [responseMessage, setResponseMessage] = useState('');
  const [isError, setIsError] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  // queue state
  const [jobId, setJobId] = useState(null);
  const [jobStatus, setJobStatus] = useState(null); // queued | in_progress | completed | failed
  const [result, setResult] = useState(null);       // output from worker (image_urls, pdf_url, ...)
  const pollRef = useRef(null);

  // existing
  const [lastPayload, setLastPayload] = useState(null);
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkMode(true);
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  }, []);

  useEffect(() => {
    return () => {
      if (pollRef.current) clearInterval(pollRef.current);
    };
  }, []);

  const handlePlatformChange = (e) => {
    const { name, checked } = e.target;
    setPlatforms((prev) => ({ ...prev, [name]: checked }));
  };

  const handleSelectAll = () => {
    setPlatforms({ instagram: true, x: true, linkedin: true, facebook: true });
  };

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    if (newMode) document.body.classList.add('dark');
    else document.body.classList.remove('dark');
  };

  const handleLogout = async () => {
    await new Promise((r) => setTimeout(r, 300));
    localStorage.removeItem('token');
    localStorage.removeItem('tokenExpiry');
    localStorage.removeItem('username');
    localStorage.removeItem('user_id');
    window.location.href = '/login';
  };

  const validateForm = () => {
    const newErrors = {};
    if (!prompt.trim()) newErrors.prompt = 'Marketing theme is required';
    if (!numImages) newErrors.numImages = 'Please select the number of images';
    if (!contentType) newErrors.contentType = 'Please select a content type';
    if (!Object.values(platforms).includes(true)) {
      newErrors.platforms = 'Please select at least one platform';
    }
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // ---- QUEUE HELPERS ----
  const extractResultFromStatus = (data) => {
    // DynamoDB item shape: { job_id, status, updated_at, meta: { result, error, ... } }
    const meta = data?.meta || {};
    return meta.result || data.result || data.output || null;
  };

  const extractErrorFromStatus = (data) => {
    const meta = data?.meta || {};
    return meta.error || data.error || null;
  };

  const pollStatus = (id) => {
    if (pollRef.current) clearInterval(pollRef.current);
    pollRef.current = setInterval(async () => {
      try {
        const { data } = await axios.get(`${API}/queue/status/${id}`);
        // status: queued | in_progress | completed | failed
        setJobStatus(data.status);

        if (data.status === 'completed') {
          const r = extractResultFromStatus(data);
          setResult(r);
          setResponseMessage('üéâ Content generated successfully!');
          setIsError(false);
          setIsLoading(false);
          clearInterval(pollRef.current);
          pollRef.current = null;
        } else if (data.status === 'failed') {
          const errText = extractErrorFromStatus(data) || 'Job failed. Please try again.';
          setIsLoading(false);
          setIsError(true);
          setResponseMessage(errText);
          clearInterval(pollRef.current);
          pollRef.current = null;
        }
      } catch (err) {
        setIsLoading(false);
        setIsError(true);
        setResponseMessage('Failed to fetch job status.');
        clearInterval(pollRef.current);
        pollRef.current = null;
      }
    }, 2000);
  };

  const enqueue = async (payload) => {
    const token = localStorage.getItem('token'); // optional
    const headers = {
      'Content-Type': 'application/json',
    };
    if (token) headers['Authorization'] = `Bearer ${token}`;

    // IMPORTANT: No custom 'X-User-Id' header to avoid CORS preflight failure.
    const { data } = await axios.post(`${API}/queue/enqueue`, payload, { headers });
    return data; // { job_id, status: "queued" }
  };
  // -----------------------

  const handleSubmit = async (e, isRetry = false) => {
    e?.preventDefault();
    if (!(validateForm() || isRetry)) return;

    // find user id
    const userId =
      (user && (user.username || user.id)) ||
      localStorage.getItem('username') ||
      localStorage.getItem('user_id') ||
      '';

    if (!userId) {
      setIsError(true);
      setResponseMessage('You must be logged in. Missing user_id.');
      return;
    }

    setIsLoading(true);
    setIsError(false);
    setResponseMessage('');
    setResult(null);
    setJobStatus(null);
    setJobId(null);

    const payload = isRetry
      ? lastPayload
      : {
          prompt,
          numImages: Number(numImages),
          contentType,
          user_id: userId, // <-- pass user id in BODY
          // map "x" to backend "twitter"
          platforms: {
            instagram: platforms.instagram,
            linkedin: platforms.linkedin,
            twitter: platforms.x,
            facebook: platforms.facebook,
          },
          // optional: toggle if you later add a switch
          // useMemeMode: false,
        };

    setLastPayload(payload);

    try {
      // 1) enqueue
      const { job_id } = await enqueue(payload);
      setJobId(job_id);
      setJobStatus('queued');
      setResponseMessage('‚úÖ Request queued. We‚Äôll update you as it processes‚Ä¶');

      // 2) poll
      pollStatus(job_id);
    } catch (err) {
      setIsLoading(false);
      setIsError(true);
      const msg =
        err?.response?.data?.error ||
        err?.message ||
        'Failed to enqueue job. Please try again.';
      setResponseMessage(msg);
    }
  };

  const handleReset = () => {
    setPrompt('');
    setNumImages('');
    setContentType('');
    setPlatforms({ instagram: false, x: false, linkedin: false, facebook: false });
    setErrors({});
    setResponseMessage('');
    setIsError(false);
    setLastPayload(null);
    setResult(null);
    setJobStatus(null);
    setJobId(null);
    if (pollRef.current) clearInterval(pollRef.current);
  };

  const statusLabel = (() => {
    if (jobStatus === 'in_progress') return 'Processing‚Ä¶';
    if (jobStatus === 'queued') return 'Queued‚Ä¶';
    if (jobStatus === 'completed') return 'Completed';
    if (jobStatus === 'failed') return 'Failed';
    return jobStatus || '‚Äî';
  })();

  const isBusy = isLoading || jobStatus === 'queued' || jobStatus === 'in_progress';

  return (
    <div className={`content-creation-container ${isDarkMode ? 'dark' : 'light'}`}>
      {/* Background Watermark Text */}
      <div className="background-text bg-text-1" style={{'--rotate': '-15deg'}}>CREATE</div>
      <div className="background-text bg-text-2" style={{'--rotate': '12deg'}}>AUTOMATE</div>
      <div className="background-text bg-text-3" style={{'--rotate': '-8deg'}}>SOCIAL</div>
      <div className="background-text bg-text-4" style={{'--rotate': '20deg'}}>AI CONTENT</div>
      <div className="background-text bg-text-5" style={{'--rotate': '-18deg'}}>POST</div>

      {/* Tech Words */}
      <div className="tech-words tech-word-1">&lt;/&gt; GENERATE</div>
      <div className="tech-words tech-word-2">[ MARKETING ]</div>
      <div className="tech-words tech-word-3">{'{ ENGAGE }'}</div>
      <div className="tech-words tech-word-4">AI POWERED</div>

      {/* Floating Icons */}
      <div className="extra-icons">
        <span className="icon1">üöÄ</span>
        <span className="icon2">ü§ñ</span>
        <span className="icon3">üìä</span>
        <span className="icon4">üí°</span>
      </div>

      {/* Theme Toggle */}
      <button className="theme-toggle top-left" onClick={toggleTheme}>
        {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
      </button>

      {/* Logout Button */}
      <button className="logout-button top-right" onClick={handleLogout}>üö™</button>

      <div className="content-creation-box">
        <div className="header-section">
          <h1 className="content-creation-header">CraftingBrain</h1>
          <h2 className="content-creation-subheader">AI-Powered Content Creation</h2>
          <div className="header-decoration"></div>
        </div>

        {/* Info Card */}
        <div className="info-card">
          <span className="info-card-icon">‚ú®</span>
          <div className="info-card-text">
            <strong>Create Your Content with Just One Click!</strong>
            Transform your ideas into stunning social media posts instantly. Our AI analyzes your theme,
            generates professional visuals, and publishes directly to your chosen platforms.
            <div className="info-highlights">
              <span className="info-highlight">‚ö° Lightning Fast</span>
              <span className="info-highlight">üé® Pro Quality</span>
              <span className="info-highlight">üöÄ Auto-Post</span>
            </div>
          </div>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="prompt">Marketing Theme üéØ</label>
            <input
              type="text"
              id="prompt"
              value={prompt}
              onChange={(e) => setPrompt(e.target.value)}
              placeholder="E.g., Promote eco-friendly products, AI technology trends..."
              required
            />
            {errors.prompt && <span className="error-message">{errors.prompt}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="numImages">Number of Images üì∏</label>
            <select
              id="numImages"
              value={numImages}
              onChange={(e) => setNumImages(e.target.value)}
              required
            >
              <option value="" disabled>Choose number of images</option>
              {[1, 2, 3, 4, 5].map(n => <option key={n} value={n}>{n} Image{n > 1 ? 's' : ''}</option>)}
            </select>
            {errors.numImages && <span className="error-message">{errors.numImages}</span>}
          </div>

          <div className="form-group">
            <label htmlFor="contentType">Content Type üìù</label>
            <select
              id="contentType"
              value={contentType}
              onChange={(e) => setContentType(e.target.value)}
              required
            >
              <option value="" disabled>Choose content style</option>
              {[
                { value: "Informative", label: "üìö Informative - Educational & factual" },
                { value: "Inspirational", label: "üí´ Inspirational - Motivating & uplifting" },
                { value: "Promotional", label: "üéâ Promotional - Sales & marketing" },
                { value: "Educational", label: "üéì Educational - Teaching & training" },
                { value: "Engaging", label: "üî• Engaging - Interactive & fun" }
              ].map((type) => (
                <option key={type.value} value={type.value}>{type.label}</option>
              ))}
            </select>
            {errors.contentType && <span className="error-message">{errors.contentType}</span>}
          </div>

          <div className="form-group">
            <label>Target Platforms üåê</label>
            <div className="platform-grid">
              {[
                { key: 'instagram', icon: 'üì∑', name: 'Instagram' },
                { key: 'x', icon: 'ùïè', name: 'X (Twitter)' },
                { key: 'linkedin', icon: 'üíº', name: 'LinkedIn' },
                { key: 'facebook', icon: 'üë•', name: 'Facebook' }
              ].map(({ key, icon, name }) => (
                <label key={key} className={`platform-card ${platforms[key] ? 'selected' : ''}`}>
                  <input
                    type="checkbox"
                    name={key}
                    checked={platforms[key]}
                    onChange={handlePlatformChange}
                    className="platform-checkbox"
                  />
                  <div className="platform-content">
                    <span className="platform-icon">{icon}</span>
                    <span className="platform-name">{name}</span>
                  </div>
                </label>
              ))}
            </div>
            <button type="button" className="select-all-button" onClick={handleSelectAll}>
              ‚úì Select All Platforms
            </button>
            {errors.platforms && <span className="error-message">{errors.platforms}</span>}
          </div>

          <div className="form-actions">
            <button type="submit" className="post-button" disabled={isBusy}>
              {isBusy ? (
                <>
                  <span className="spinner"></span>
                  <span>{statusLabel}</span>
                </>
              ) : (
                'üöÄ Generate & Post'
              )}
            </button>
            <button type="button" className="reset-button" onClick={handleReset} disabled={isLoading}>
              üîÑ Reset
            </button>
          </div>
        </form>

        {/* Queue status + results */}
        {(jobId || jobStatus) && (
          <div className="queue-status">
            <p><strong>Job:</strong> {jobId || '-'}</p>
            <p><strong>Status:</strong> {statusLabel}</p>
          </div>
        )}

        {responseMessage && (
          <div className={isError ? 'response-error' : 'response-success'}>
            <div className="response-icon">{isError ? '!' : '‚úì'}</div>
            <div className="response-content">
              <p>{responseMessage}</p>
              {isError && lastPayload && (
                <button
                  type="button"
                  className="retry-button"
                  onClick={(e) => handleSubmit(e, true)}
                  disabled={isLoading}
                >
                  {isLoading ? (
                    <>
                      <span className="spinner"></span>
                      <span>Retrying...</span>
                    </>
                  ) : (
                    'üîÑ Retry'
                  )}
                </button>
              )}
            </div>
          </div>
        )}

        {/* Optional: show generated assets if backend returns them */}
        {result?.image_urls?.length > 0 && (
          <div className="generated-preview">
            <h3>Generated Images</h3>
            <div className="preview-grid">
              {result.image_urls.map((url, i) => (
                <a key={i} href={url} target="_blank" rel="noreferrer">
                  <img src={url} alt={`gen-${i}`} style={{ maxWidth: 160, borderRadius: 8 }} />
                </a>
              ))}
            </div>
          </div>
        )}
        {result?.pdf_url && (
          <div className="generated-preview">
            <h3>LinkedIn/PDF</h3>
            <a href={result.pdf_url} target="_blank" rel="noreferrer">{result.pdf_url}</a>
          </div>
        )}
      </div>
    </div>
  );
};

export default ContentCreation;
