// src/components/Register.js - Futuristic AI Version
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
    confirmPassword: ''
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState(0);
  
  // Password visibility states
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem('theme');
    if (saved === 'dark' || !saved) {
      setIsDarkMode(true);
      document.body.classList.add('dark');
    }

    const token = localStorage.getItem('token');
    if (token) {
      const tokenExpiry = localStorage.getItem('tokenExpiry');
      if (tokenExpiry && parseInt(tokenExpiry) > Date.now()) {
        navigate('/connect', { replace: true });
      } else {
        localStorage.removeItem('token');
        localStorage.removeItem('tokenExpiry');
      }
    }
  }, [navigate]);

  useEffect(() => {
    if (formData.password) {
      calculatePasswordStrength(formData.password);
    }
  }, [formData.password]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    
    if (newMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  };

  const calculatePasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 6) strength++;
    if (password.length >= 10) strength++;
    if (/[a-z]/.test(password) && /[A-Z]/.test(password)) strength++;
    if (/\d/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;
    setPasswordStrength(strength);
  };

  const getPasswordStrengthLabel = () => {
    if (passwordStrength === 0) return '';
    if (passwordStrength <= 2) return 'Weak';
    if (passwordStrength <= 3) return 'Fair';
    if (passwordStrength <= 4) return 'Good';
    return 'Strong';
  };

  const getPasswordStrengthColor = () => {
    if (passwordStrength <= 2) return '#ef4444';
    if (passwordStrength <= 3) return '#f59e0b';
    if (passwordStrength <= 4) return '#10b981';
    return '#22d3ee';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
    
    if (error) setError('');
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = 'Name is required';
    } else if (formData.name.trim().length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      errors.email = 'Please enter a valid email address';
    }

    if (!formData.username.trim()) {
      errors.username = 'Username is required';
    } else if (formData.username.trim().length < 3) {
      errors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      errors.username = 'Username can only contain letters, numbers, and underscores';
    }

    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }

    setValidationErrors({});
    setIsLoading(true);

    try {
      const response = await fetch(
        'https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/register',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: formData.name.trim(),
            email: formData.email.toLowerCase().trim(),
            username: formData.username.trim(),
            password: formData.password,
            confirmPassword: formData.confirmPassword 
          }),
        }
      );

      const data = await response.json();
      console.log('Registration response:', { status: response.status, data });

      if (response.ok) {
        setSuccess('Registration successful! Taking you to the survey...');
        
        localStorage.setItem('registeredUserId', formData.username.trim());
        localStorage.setItem('username', formData.username.trim());
        
        if (data.token) {
          localStorage.setItem('token', data.token);
        }
        
        console.log('Saved to localStorage:', {
          registeredUserId: formData.username.trim(),
          username: formData.username.trim()
        });
        
        setFormData({
          name: '',
          email: '',
          username: '',
          password: '',
          confirmPassword: ''
        });
        
        setTimeout(() => {
          navigate('/survey');
        }, 1500);

      } else {
        if (data.error) {
          const errorMessage = data.error.toLowerCase();
          
          if (errorMessage.includes('username') && (errorMessage.includes('already') || errorMessage.includes('exists'))) {
            setValidationErrors({ 
              username: 'Username already taken. Please choose a different username.' 
            });
            setError('');
          } 
          else if (errorMessage.includes('email') && (errorMessage.includes('already') || errorMessage.includes('registered'))) {
            setValidationErrors({ 
              email: 'This email is already registered. Please use a different email or try logging in.' 
            });
            setError('');
          } 
          else if (errorMessage.includes('user') && errorMessage.includes('already')) {
            setError('An account with these details already exists. Please try logging in instead.');
          }
          else if (errorMessage.includes('password') && errorMessage.includes('match')) {
            setValidationErrors({
              confirmPassword: 'Passwords do not match'
            });
            setError('');
          }
          else if (errorMessage.includes('invalid') && errorMessage.includes('email')) {
            setValidationErrors({
              email: 'Please enter a valid email address'
            });
            setError('');
          }
          else if (errorMessage.includes('invalid') && errorMessage.includes('username')) {
            setValidationErrors({
              username: 'Username can only contain letters, numbers, and underscores'
            });
            setError('');
          }
          else {
            setError(data.error);
          }
        } 
        else {
          if (response.status === 409) {
            setError('User already exists. Please try logging in or use different credentials.');
          } else if (response.status === 400) {
            setError('Invalid registration data. Please check all fields and try again.');
          } else if (response.status === 500) {
            setError('Server error occurred. Please try again later.');
          } else {
            setError('Registration failed. Please try again.');
          }
        }
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError('Network error. Please check your connection and try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="futuristic-register-container">
      <div className="cyber-grid"></div>
      <div className="floating-particles">
        {[...Array(20)].map((_, i) => (
          <div key={i} className="particle"></div>
        ))}
      </div>

      <button className="theme-toggle-futuristic" onClick={toggleTheme}>
        {isDarkMode ? 'â˜€ï¸' : 'ðŸŒ™'}
      </button>

      <button className="logout-button-futuristic" onClick={() => navigate('/login')}>
        Logout â†’
      </button>

      <div className="extra-icons">
        <span className="icon1">ðŸš€</span>
        <span className="icon2">ðŸ¤–</span>
        <span className="icon3">ðŸ“Š</span>
        <span className="icon4">ðŸ’¡</span>
      </div>

      <div className="register-content-wrapper">
        {/* Left Side - Marketing Section */}
        <div className="marketing-section-futuristic">
          <div className="brand-header-futuristic">
            <div className="brand-main">
              <div className="logo-container">
                <img src="/123.png" alt="Marketing Bot Logo" className="brand-logo-futuristic" />
                <div className="logo-glow"></div>
              </div>
              <div className="brand-info-futuristic">
                <h1>Marketing Bot</h1>
                <p className="brand-tagline">AI Powered</p>
              </div>
            </div>
          </div>

          <div className="hero-typing-container">
            <h1 className="hero-title">
              <span className="gradient-text">START YOUR</span>
              <span className="gradient-text">MARKETING</span>
              <span className="gradient-text">REVOLUTION</span>
              <span className="cursor-blink">|</span>
            </h1>
            <p className="hero-subtitle">
              Transform your business with the power of AI. Join 1000+ forward-thinking entrepreneurs 
              who are already dominating social media. One platform, unlimited possibilities â€“ create, 
              schedule, and scale your brand like never before.
            </p>
          </div>

          <div className="stats-futuristic">
            <div className="stat-item-futuristic">
              <div className="stat-value">10+</div>
              <div className="stat-label">Active Users</div>
            </div>
            <div className="stat-item-futuristic">
              <div className="stat-value">50+</div>
              <div className="stat-label">Posts</div>
            </div>
            <div className="stat-item-futuristic">
              <div className="stat-value">98%</div>
              <div className="stat-label">Success</div>
            </div>
          </div>

          <div className="features-futuristic">
            <div className="feature-card-futuristic">
              <div className="feature-icon-futuristic">ðŸŽ¨</div>
              <h4>AI Content Generation</h4>
              <p>Create compelling content that resonates with your audience using advanced AI algorithms.</p>
            </div>
            <div className="feature-card-futuristic">
              <div className="feature-icon-futuristic">ðŸ“Š</div>
              <h4>Real-time Analytics</h4>
              <p>Track performance metrics and gain insights to optimize your marketing strategy instantly.</p>
            </div>
            <div className="feature-card-futuristic">
              <div className="feature-icon-futuristic">âš¡</div>
              <h4>Smart Automation</h4>
              <p>Automate repetitive tasks and focus on what matters â€“ growing your business.</p>
            </div>
          </div>
        </div>

        {/* Right Side - Registration Form */}
        <div className="register-form-section">
          <div className="form-container-futuristic">
            <div className="form-header-futuristic">
              <h2>Create Your Account</h2>
              <p>Start your free journey today</p>
            </div>

            <div className="info-card-futuristic">
              <span className="alert-icon">â„¹ï¸</span>
              <p>After registration, complete a quick survey to personalize your content.</p>
            </div>

            <form className="futuristic-form" onSubmit={handleSubmit}>
              <div className="input-group-futuristic">
                <label htmlFor="name">Full Name *</label>
                <div className="input-wrapper">
                  <input
                    id="name"
                    type="text"
                    name="name"
                    placeholder="Enter your full name"
                    value={formData.name}
                    onChange={handleInputChange}
                    className={validationErrors.name ? 'error' : ''}
                    required
                  />
                </div>
                {validationErrors.name && <span className="field-error-futuristic">{validationErrors.name}</span>}
              </div>

              <div className="input-group-futuristic">
                <label htmlFor="email">Email Address *</label>
                <div className="input-wrapper">
                  <input
                    id="email"
                    type="email"
                    name="email"
                    placeholder="your.email@example.com"
                    value={formData.email}
                    onChange={handleInputChange}
                    className={validationErrors.email ? 'error' : ''}
                    required
                  />
                </div>
                {validationErrors.email && <span className="field-error-futuristic">{validationErrors.email}</span>}
              </div>

              <div className="input-group-futuristic">
                <label htmlFor="username">Username *</label>
                <div className="input-wrapper">
                  <input
                    id="username"
                    type="text"
                    name="username"
                    placeholder="Choose a unique username"
                    value={formData.username}
                    onChange={handleInputChange}
                    className={validationErrors.username ? 'error' : ''}
                    required
                  />
                </div>
                {validationErrors.username && <span className="field-error-futuristic">{validationErrors.username}</span>}
                <small className="field-hint-futuristic">Letters, numbers, and underscores only. Min 3 characters.</small>
              </div>

              <div className="input-group-futuristic">
                <label htmlFor="password">Password *</label>
                <div className="password-wrapper-futuristic">
                  <div className="input-wrapper">
                    <input
                      id="password"
                      type={showPassword ? "text" : "password"}
                      name="password"
                      placeholder="Create a strong password"
                      value={formData.password}
                      onChange={handleInputChange}
                      className={validationErrors.password ? 'error' : ''}
                      required
                    />
                  </div>
                  <button
                    type="button"
                    className="password-toggle-futuristic"
                    onClick={() => setShowPassword(!showPassword)}
                    aria-label={showPassword ? "Hide password" : "Show password"}
                  >
                    {showPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
                  </button>
                </div>
                {formData.password && (
                  <div className="password-strength-bar">
                    <div className="strength-fill" style={{ width: `${(passwordStrength / 5) * 100}%`, backgroundColor: getPasswordStrengthColor() }}></div>
                    <span className="strength-label" style={{ color: getPasswordStrengthColor() }}>{getPasswordStrengthLabel()}</span>
                  </div>
                )}
                {validationErrors.password && <span className="field-error-futuristic">{validationErrors.password}</span>}
                <small className="field-hint-futuristic">Use uppercase, lowercase, numbers & symbols for better security.</small>
              </div>

              <div className="input-group-futuristic">
                <label htmlFor="confirmPassword">Confirm Password *</label>
                <div className="password-wrapper-futuristic">
                  <div className="input-wrapper">
                    <input
                      id="confirmPassword"
                      type={showConfirmPassword ? "text" : "password"}
                      name="confirmPassword"
                      placeholder="Confirm your password"
                      value={formData.confirmPassword}
                      onChange={handleInputChange}
                      className={validationErrors.confirmPassword ? 'error' : ''}
                      required
                    />
                  </div>
                  <button
                    type="button"
                    className="password-toggle-futuristic"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    aria-label={showConfirmPassword ? "Hide password" : "Show password"}
                  >
                    {showConfirmPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
                  </button>
                </div>
                {validationErrors.confirmPassword && <span className="field-error-futuristic">{validationErrors.confirmPassword}</span>}
              </div>

              {error && (
                <div className="alert-futuristic error-alert">
                  <span className="alert-icon">!</span>
                  {error}
                </div>
              )}
              
              {success && (
                <div className="alert-futuristic success-alert">
                  <span className="alert-icon">âœ“</span>
                  {success}
                </div>
              )}

              <button type="submit" className="submit-btn-futuristic" disabled={isLoading}>
                {isLoading ? <div className="loading-spinner"></div> : 'Create Account'}
              </button>
            </form>

            <div className="form-footer-futuristic">
              <p>Already have an account? <a href="/login" onClick={(e) => { e.preventDefault(); navigate('/login'); }}>Login here</a></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
