// src/components/Register.js
import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '', email: '', username: '', password: '', confirmPassword: ''
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [showRegisterModal, setShowRegisterModal] = useState(true); // keep popup
  const [typedText, setTypedText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);

  const navigate = useNavigate();

  const phrases = useMemo(() => [
    'Join the Revolution',
    'Start Creating Content',
    'Automate Your Marketing',
    'Grow Your Business',
    'AI-Powered Success'
  ], []);

  // typewriter
  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting ? fullText.substring(0, typedText.length - 1)
                   : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 1400);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  // strength
  useEffect(() => {
    if (formData.password) {
      let s=0;
      if (formData.password.length >= 6) s++;
      if (formData.password.length >= 10) s++;
      if (/[a-z]/.test(formData.password) && /[A-Z]/.test(formData.password)) s++;
      if (/\d/.test(formData.password)) s++;
      if (/[^a-zA-Z0-9]/.test(formData.password)) s++;
      setPasswordStrength(s);
    } else setPasswordStrength(0);
  }, [formData.password]);

  // modal: scroll lock + ESC
  useEffect(() => {
    const lock = () => document.body.style.overflow = 'hidden';
    const unlock = () => document.body.style.overflow = '';
    if (showRegisterModal) lock(); else unlock();

    const onKey = (e) => { if (e.key === 'Escape') closeRegisterModal(); };
    window.addEventListener('keydown', onKey);
    return () => { window.removeEventListener('keydown', onKey); unlock(); };
  }, [showRegisterModal]);

  const getPasswordStrengthColor = () => {
    if (passwordStrength <= 2) return '#ef4444';
    if (passwordStrength <= 3) return '#f59e0b';
    if (passwordStrength <= 4) return '#10b981';
    return '#22d3ee';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    if (validationErrors[name]) setValidationErrors(prev => ({ ...prev, [name]: '' }));
    if (error) setError('');
  };

  const validateForm = () => {
    const errors = {};
    if (!formData.name.trim()) errors.name = 'Name is required';
    else if (formData.name.trim().length < 2) errors.name = 'Name must be at least 2 characters';

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) errors.email = 'Email is required';
    else if (!emailRegex.test(formData.email)) errors.email = 'Please enter a valid email address';

    if (!formData.username.trim()) errors.username = 'Username is required';
    else if (formData.username.trim().length < 3) errors.username = 'Username must be at least 3 characters';
    else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) errors.username = 'Only letters, numbers, and underscores';

    if (!formData.password) errors.password = 'Password is required';
    else if (formData.password.length < 6) errors.password = 'Min 6 characters';

    if (!formData.confirmPassword) errors.confirmPassword = 'Please confirm your password';
    else if (formData.password !== formData.confirmPassword) errors.confirmPassword = 'Passwords do not match';

    return errors;
  };

  const closeRegisterModal = () => {
    setShowRegisterModal(false);
    setError('');
    setSuccess('');
    navigate('/login');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(''); setSuccess('');
    const errors = validateForm();
    if (Object.keys(errors).length > 0) { setValidationErrors(errors); return; }
    setValidationErrors({}); setIsLoading(true);

    try {
      const response = await fetch(
        'https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/register',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: formData.name.trim(),
            email: formData.email.toLowerCase().trim(),
            username: formData.username.trim(),
            password: formData.password,
            confirmPassword: formData.confirmPassword
          }),
        }
      );
      const data = await response.json();

      if (response.ok) {
        setSuccess('Registration successful! Taking you to the survey...');
        localStorage.setItem('registeredUserId', formData.username.trim());
        localStorage.setItem('username', formData.username.trim());
        if (data.token) localStorage.setItem('token', data.token);

        setFormData({ name:'', email:'', username:'', password:'', confirmPassword:'' });
        setTimeout(()=> navigate('/survey'), 1200);
      } else {
        if (data.error) {
          const msg = data.error.toLowerCase();
          if (msg.includes('username') && (msg.includes('already') || msg.includes('exists')))
            setValidationErrors({ username: 'Username already taken. Choose a different one.' });
          else if (msg.includes('email') && (msg.includes('already') || msg.includes('registered')))
            setValidationErrors({ email: 'Email already registered. Try login instead.' });
          else setError(data.error);
        } else {
          if (response.status === 409) setError('User already exists. Try logging in.');
          else if (response.status === 400) setError('Invalid data. Please check all fields.');
          else setError('Registration failed. Please try again.');
        }
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError('Network error. Please check your connection and try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // simple navbar (kept light)
  const navbarStyles = {
    position:'fixed', top:0, left:0, right:0, zIndex:9999,
    background:'rgba(255,255,255,.06)', borderBottom:'1px solid rgba(255,255,255,.08)',
    backdropFilter:'blur(16px)', WebkitBackdropFilter:'blur(16px)',
    padding:'1rem 2rem', display:'flex', alignItems:'center', justifyContent:'space-between'
  };
  const leftSectionStyles = { display:'flex', alignItems:'center', gap:'1.2rem' };
  const logoStyles = {
    width:46, height:46, borderRadius:12, objectFit:'cover',
    background:'linear-gradient(135deg,#7C3AED,#A78BFA)', padding:2
  };
  const brandTextStyles = {
    fontSize:'1.6rem', fontWeight:900,
    background:'linear-gradient(135deg,#7C3AED,#A78BFA)',
    WebkitBackgroundClip:'text', WebkitTextFillColor:'transparent'
  };
  const navLinksStyles = { display:'flex', gap:'2rem', alignItems:'center' };
  const navLinkStyles = { color:'#e6e9f8', textDecoration:'none', fontWeight:700 };

  const modalOverlayStyles = {
    position:'fixed', inset:0, display:'flex', alignItems:'center', justifyContent:'center',
  background:'rgba(3,6,20,.55)', backdropFilter:'blur(14px)', zIndex:9998,
    opacity: showRegisterModal ? 1 : 0, visibility: showRegisterModal ? 'visible' : 'hidden',
    transition:'opacity .35s ease, visibility .35s ease'
  };

  return (
    <div className="register-page-container">
      <div className="cyber-grid"></div>

      {/* NAVBAR */}
      <nav style={navbarStyles}>
        <div style={leftSectionStyles}>
          <img src="/123.png" alt="Posting Expert" style={logoStyles}
               onError={(e)=>{ e.target.style.display='none'; }}/>
          <span style={brandTextStyles}>Posting Expert</span>
        </div>
        <div style={navLinksStyles}>
          <Link to="/login" style={navLinkStyles}>Home</Link>
          <Link to="/about" style={navLinkStyles}>About</Link>
          <Link to="/privacy-policy" style={navLinkStyles}>Privacy Policy</Link>
          <Link to="/contact-us" style={navLinkStyles}>Contact Us</Link>
        </div>
        <div>
          <button
            onClick={()=>navigate('/login')}
            type="button"
            className="submit-btn-register"
            style={{width:'auto', padding:'.8rem 1.2rem'}}
          >
            Back to Login
          </button>
        </div>
      </nav>

      {/* REGISTER MODAL */}
      <div style={modalOverlayStyles}>
        <div className="register-modal" role="dialog" aria-modal="true" aria-label="Create account">
          <button className="register-modal-close" onClick={closeRegisterModal} type="button" title="Close (Esc)">‚úï</button>

          <div className="form-header-register">
            <h2>Create Your Account</h2>
            <p>Join thousands of successful marketers</p>
          </div>

          <form className="register-form" onSubmit={handleSubmit}>
            <div className="input-group-register">
              <label>FULL NAME</label>
              <div className="input-wrapper">
                <input
                  type="text" name="name" placeholder="Enter your full name"
                  value={formData.name} onChange={handleInputChange}
                  className={validationErrors.name ? 'error' : ''} autoComplete="off" required
                />
              </div>
              {validationErrors.name && <span className="field-error">{validationErrors.name}</span>}
            </div>

            <div className="input-group-register">
              <label>EMAIL ADDRESS</label>
              <div className="input-wrapper">
                <input
                  type="email" name="email" placeholder="your.email@example.com"
                  value={formData.email} onChange={handleInputChange}
                  className={validationErrors.email ? 'error' : ''} autoComplete="off" required
                />
              </div>
              {validationErrors.email && <span className="field-error">{validationErrors.email}</span>}
            </div>

            <div className="input-group-register">
              <label>USERNAME</label>
              <div className="input-wrapper">
                <input
                  type="text" name="username" placeholder="Choose a unique username"
                  value={formData.username} onChange={handleInputChange}
                  className={validationErrors.username ? 'error' : ''} autoComplete="off" required
                />
              </div>
              {validationErrors.username && <span className="field-error">{validationErrors.username}</span>}
              <small className="field-hint">Letters, numbers, and underscores only. Min 3 characters.</small>
            </div>

            <div className="input-group-register">
              <label>PASSWORD</label>
              <div className="password-wrapper-register">
                <input
                  type={showPassword ? "text" : "password"} name="password"
                  placeholder="Create a strong password"
                  value={formData.password} onChange={handleInputChange}
                  className={validationErrors.password ? 'error' : ''} autoComplete="off" required
                />
                <button type="button" className="password-toggle-register"
                        onClick={() => setShowPassword(!showPassword)} aria-label="Toggle password visibility">
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
              {formData.password && (
                <div className="password-strength-indicator">
                  <div className="strength-bar">
                    <div className="strength-fill"
                         style={{ width: `${(passwordStrength/5)*100}%`, background: getPasswordStrengthColor() }} />
                  </div>
                  <span style={{ color: getPasswordStrengthColor() }}>
                    {passwordStrength === 0 ? '' : passwordStrength <= 2 ? 'Weak' : passwordStrength <= 3 ? 'Fair' : passwordStrength <= 4 ? 'Good' : 'Strong'}
                  </span>
                </div>
              )}
              {validationErrors.password && <span className="field-error">{validationErrors.password}</span>}
              <small className="field-hint">Use uppercase, lowercase, numbers & symbols.</small>
            </div>

            <div className="input-group-register">
              <label>CONFIRM PASSWORD</label>
              <div className="password-wrapper-register">
                <input
                  type={showConfirmPassword ? "text" : "password"} name="confirmPassword"
                  placeholder="Confirm your password"
                  value={formData.confirmPassword} onChange={handleInputChange}
                  className={validationErrors.confirmPassword ? 'error' : ''} autoComplete="off" required
                />
                <button type="button" className="password-toggle-register"
                        onClick={() => setShowConfirmPassword(!showConfirmPassword)} aria-label="Toggle password visibility">
                  {showConfirmPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
              {validationErrors.confirmPassword && <span className="field-error">{validationErrors.confirmPassword}</span>}
            </div>

            {error && <div className="alert-register error-alert"><span className="alert-icon">‚ö†Ô∏è</span><span>{error}</span></div>}
            {success && <div className="alert-register success-alert"><span className="alert-icon">‚úì</span><span>{success}</span></div>}

            <button type="submit" className="submit-btn-register" disabled={isLoading}>
              {isLoading ? (<><span className="loading-spinner"></span><span>Processing...</span></>) : (<><span>CREATE ACCOUNT</span><span className="btn-arrow">‚Üí</span></>)}
              <div className="btn-glow"></div>
            </button>
          </form>

          <div className="form-footer-register">
            <p>Already have an account? <Link to="/login">Login here</Link></p>
          </div>
        </div>
      </div>

      {/* Simple footer */}
      <footer className="premium-footer">
        <div className="footer-bottom">
          <p>¬© 2025 Posting Expert. All rights reserved. | Powered by Inikola Technologies & CraftingBrain</p>
        </div>
      </footer>
    </div>
  );
};

export default Register;
