// Register.js - UPDATED WITH CANCEL BUTTON AND RESPONSIVE DESIGN
import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
    confirmPassword: ''
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [showRegisterModal, setShowRegisterModal] = useState(true);
  const [typedText, setTypedText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);
  
  const navigate = useNavigate();

  const phrases = useMemo(() => [
    'Join the Revolution',
    'Start Creating Content',
    'Automate Your Marketing',
    'Grow Your Business',
    'AI-Powered Success'
  ], []);

  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting
          ? fullText.substring(0, typedText.length - 1)
          : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 2000);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  useEffect(() => {
    if (formData.password) {
      calculatePasswordStrength(formData.password);
    } else {
      setPasswordStrength(0);
    }
  }, [formData.password]);

  const calculatePasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 6) strength++;
    if (password.length >= 10) strength++;
    if (/[a-z]/.test(password) && /[A-Z]/.test(password)) strength++;
    if (/\d/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;
    setPasswordStrength(strength);
  };

  const getPasswordStrengthLabel = () => {
    if (passwordStrength === 0) return '';
    if (passwordStrength <= 2) return 'Weak';
    if (passwordStrength <= 3) return 'Fair';
    if (passwordStrength <= 4) return 'Good';
    return 'Strong';
  };

  const getPasswordStrengthColor = () => {
    if (passwordStrength <= 2) return '#ef4444';
    if (passwordStrength <= 3) return '#f59e0b';
    if (passwordStrength <= 4) return '#10b981';
    return '#22d3ee';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
    
    if (error) setError('');
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = 'Name is required';
    } else if (formData.name.trim().length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      errors.email = 'Please enter a valid email address';
    }

    if (!formData.username.trim()) {
      errors.username = 'Username is required';
    } else if (formData.username.trim().length < 3) {
      errors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      errors.username = 'Username can only contain letters, numbers, and underscores';
    }

    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    return errors;
  };

  const closeRegisterModal = () => {
    setShowRegisterModal(false);
    setError('');
    setSuccess('');
    navigate('/login');
  };

  const handleCancel = () => {
    closeRegisterModal();
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }

    setValidationErrors({});
    setIsLoading(true);

    try {
      const response = await fetch(
        'https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/register',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: formData.name.trim(),
            email: formData.email.toLowerCase().trim(),
            username: formData.username.trim(),
            password: formData.password,
            confirmPassword: formData.confirmPassword 
          }),
        }
      );

      const data = await response.json();

      if (response.ok) {
        setSuccess('Registration successful! Taking you to the survey...');
        
        localStorage.setItem('registeredUserId', formData.username.trim());
        localStorage.setItem('username', formData.username.trim());
        
        if (data.token) {
          localStorage.setItem('token', data.token);
        }
        
        setFormData({
          name: '',
          email: '',
          username: '',
          password: '',
          confirmPassword: ''
        });
        
        setTimeout(() => {
          navigate('/survey');
        }, 1500);

      } else {
        if (data.error) {
          const errorMessage = data.error.toLowerCase();
          
          if (errorMessage.includes('username') && (errorMessage.includes('already') || errorMessage.includes('exists'))) {
            setValidationErrors({ 
              username: 'Username already taken. Please choose a different username.' 
            });
          } 
          else if (errorMessage.includes('email') && (errorMessage.includes('already') || errorMessage.includes('registered'))) {
            setValidationErrors({ 
              email: 'This email is already registered. Please use a different email or try logging in.' 
            });
          } 
          else if (errorMessage.includes('user') && errorMessage.includes('already')) {
            setError('An account with these details already exists. Please try logging in instead.');
          }
          else {
            setError(data.error);
          }
        } 
        else {
          if (response.status === 409) {
            setError('User already exists. Please try logging in or use different credentials.');
          } else if (response.status === 400) {
            setError('Invalid registration data. Please check all fields and try again.');
          } else {
            setError('Registration failed. Please try again.');
          }
        }
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError('Network error. Please check your connection and try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const socialPlatforms = [
    { name: 'LinkedIn', icon: '/images/linkedin.png', color: '#0077B5' },
    { name: 'Instagram', icon: '/images/instagram.jpg', color: '#E1306C' },
    { name: 'Facebook', icon: '/images/facebook.png', color: '#1877F2' }
  ];

  const horizontalFeatures = [
    { icon: "ðŸ”—", title: "Multi-Platform Integration", description: "Connect to LinkedIn, Instagram, Facebook, Twitter, and more." },
    { icon: "ðŸ¤–", title: "AI Content Creation", description: "Generate engaging posts and stunning images using AI." },
    { icon: "ðŸ“¤", title: "Auto Posting", description: "Schedule and publish content automatically." }
  ];

  const navbarStyles = {
    position: 'fixed',
    top: 0,
    left: 0,
    right: 0,
    background: 'rgba(15, 23, 42, 0.9)',
    backdropFilter: 'blur(20px)',
    WebkitBackdropFilter: 'blur(20px)',
    borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
    padding: '1rem 2rem',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    zIndex: 10000,
    boxShadow: '0 8px 24px rgba(0, 0, 0, 0.15)',
    transition: 'all 0.3s ease'
  };

  const leftSectionStyles = { display: 'flex', alignItems: 'center', gap: '1.2rem', flexShrink: 0 };

  const logoStyles = {
