          />
          <span style={brandTextStyles}>Posting Expert</span>
        </div>
        
        <div style={navLinksStyles}>
          <Link to="/login" style={navLinkStyles}>Home</Link>
          <Link to="/about" style={navLinkStyles}>About</Link>
          <Link to="/privacy-policy" style={navLinkStyles}>Privacy Policy</Link>
          <Link to="/contact-us" style={navLinkStyles}>Contact Us</Link>
        </div>

        <div>
          <button 
            onClick={() => navigate('/login')}
            type="button"
            style={registerButtonStyles}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = '0 12px 28px rgba(99,102,241,0.4)';
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = 'translateY(0)';
              e.target.style.boxShadow = '0 8px 20px rgba(99,102,241,0.3)';
            }}
          >
            Back to Login
          </button>
        </div>
      </nav>

      {/* REGISTER MODAL */}
      <div style={modalOverlayStyles}>
        <div className="modal-content">
          <button className="modal-close" onClick={closeRegisterModal} type="button">âœ•</button>
          
          <div className="form-header">
            <h2>Create Your Account</h2>
            <p>Join thousands of successful marketers</p>
          </div>

          <form className="register-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>FULL NAME</label>
              <input
                type="text"
                name="name"
                placeholder="Enter your full name"
                value={formData.name}
                onChange={handleInputChange}
                className={validationErrors.name ? 'error' : ''}
                autoComplete="off"
                required
              />
              {validationErrors.name && (
                <span className="field-error">{validationErrors.name}</span>
              )}
            </div>

            <div className="form-group">
              <label>EMAIL ADDRESS</label>
              <input
                type="email"
                name="email"
                placeholder="your.email@example.com"
                value={formData.email}
                onChange={handleInputChange}
                className={validationErrors.email ? 'error' : ''}
                autoComplete="off"
                required
              />
              {validationErrors.email && (
                <span className="field-error">{validationErrors.email}</span>
              )}
            </div>

            <div className="form-group">
              <label>USERNAME</label>
              <input
                type="text"
                name="username"
                placeholder="Choose a unique username"
                value={formData.username}
                onChange={handleInputChange}
                className={validationErrors.username ? 'error' : ''}
                autoComplete="off"
                required
              />
              {validationErrors.username && (
                <span className="field-error">{validationErrors.username}</span>
              )}
              <small className="field-hint">Letters, numbers, and underscores only. Min 3 characters.</small>
            </div>

            <div className="form-group">
              <label>PASSWORD</label>
              <div className="password-wrapper">
                <input
                  type={showPassword ? "text" : "password"}
                  name="password"
                  placeholder="Create a strong password"
                  value={formData.password}
                  onChange={handleInputChange}
                  className={validationErrors.password ? 'error' : ''}
                  autoComplete="off"
                  required
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
                </button>
              </div>
              {formData.password && (
                <div className="password-strength-indicator">
                  <div className="strength-bar">
                    <div 
                      className="strength-fill"
                      style={{
                        width: `${(passwordStrength / 5) * 100}%`,
                        background: getPasswordStrengthColor()
                      }}
                    ></div>
                  </div>
                  <span style={{ color: getPasswordStrengthColor(), fontSize: '0.85rem', fontWeight: '600' }}>
                    {getPasswordStrengthLabel()}
                  </span>
                </div>
              )}
              {validationErrors.password && (
                <span className="field-error">{validationErrors.password}</span>
              )}
              <small className="field-hint">Use uppercase, lowercase, numbers & symbols.</small>
            </div>

            <div className="form-group">
              <label>CONFIRM PASSWORD</label>
              <div className="password-wrapper">
                <input
                  type={showConfirmPassword ? "text" : "password"}
                  name="confirmPassword"
                  placeholder="Confirm your password"
                  value={formData.confirmPassword}
                  onChange={handleInputChange}
                  className={validationErrors.confirmPassword ? 'error' : ''}
                  autoComplete="off"
                  required
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  {showConfirmPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
                </button>
              </div>
              {validationErrors.confirmPassword && (
                <span className="field-error">{validationErrors.confirmPassword}</span>
              )}
            </div>

            {error && (
              <div className="alert alert-error">
                <span className="alert-icon">âš ï¸</span>
                <span>{error}</span>
              </div>
            )}
            
            {success && (
              <div className="alert alert-success">
                <span className="alert-icon">âœ“</span>
                <span>{success}</span>
              </div>
            )}

            <div className="form-actions">
              <button 
                type="button"
                className="btn btn-secondary"
                onClick={handleCancel}
                disabled={isLoading}
              >
                Cancel
              </button>
              <button 
                type="submit" 
                className="btn btn-primary" 
                disabled={isLoading}
              >
                {isLoading ? (
                  <>
                    <span className="loading-spinner"></span>
                    <span>Processing...</span>
                  </>
                ) : (
                  <>
                    <span>CREATE ACCOUNT</span>
                    <span className="btn-arrow">â†’</span>
                  </>
                )}
              </button>
            </div>
          </form>

          <div className="form-footer" style={{textAlign: 'center', marginTop: '1.5rem', color: 'var(--text-secondary)'}}>
            <p>
              Already have an account?{' '}
              <Link to="/login" style={{color: 'var(--accent-primary)', fontWeight: '600'}}>Login here</Link>
            </p>
          </div>
        </div>
      </div>

      <div className="content-wrapper">
        <div className="hero">
          <div className="brand-header">
            <div className="brand-presents">Powered by Inikola Technologies</div>
            <div className="brand-main">
              <div className="logo-container">
                <img src="/123.png" alt="Posting Expert" className="brand-logo" />
              </div>
              <div className="brand-info">
                <h1>Posting Expert</h1>
                <p className="brand-tagline">AI-Powered Social Media Automation</p>
              </div>
            </div>
          </div>

          <div className="hero-typing-container">
            <h2 className="hero-title">
              <span className="gradient-text">{typedText}</span>
              <span className="cursor-blink">|</span>
            </h2>
            <p className="hero-subtitle">
              Transform your social media strategy with cutting-edge AI technology. 
              Generate stunning content, schedule posts, and grow your presence.
            </p>
          </div>

          <div className="horizontal-features-section">
            <h3 className="section-title">
              <span style={{display: 'inline-block', animation: 'float 3s ease-in-out infinite'}}>ðŸš€</span> Powerful Features
            </h3>
            <p className="section-subtitle">Everything you need to automate your marketing</p>
            
            <div className="horizontal-features-container">
              {horizontalFeatures.map((feature, index) => (
                <div key={index} className="card horizontal-feature-card">
                  <div className="horizontal-feature-icon">
                    {feature.icon}
                  </div>
                  <h4>{feature.title}</h4>
                  <p>{feature.description}</p>
                </div>
              ))}
            </div>
          </div>

          <div className="integrations-section">
            <h3 className="section-title">
              <span style={{display: 'inline-block', animation: 'float 3s ease-in-out infinite'}}>ðŸ”—</span> Seamless Integrations
            </h3>
            <p className="section-subtitle">Connect with your favorite platforms instantly</p>
            
            <div className="platforms-orbit" style={{
              position: 'relative', 
              width: '520px', 
              height: '520px', 
              margin: '0 auto',
              maxWidth: '100%'
            }}>
              <div className="orbit-center" style={{
                position: 'absolute', 
                top: '50%', 
                left: '50%', 
                transform: 'translate(-50%,-50%)',
                width: '110px', 
                height: '110px', 
                borderRadius: '50%', 
                background: 'var(--bg-secondary)', 
                border: '2px solid var(--border-color)',
                display: 'grid', 
                placeItems: 'center', 
                zIndex: 2, 
                boxShadow: 'var(--shadow-card)', 
                animation: 'float 3s ease-in-out infinite'
              }}>
                <div className="center-logo" style={{fontSize: '2.4rem', color: 'var(--accent-primary)'}}>ðŸ¤–</div>
              </div>
              
              {socialPlatforms.map((platform, index) => (
                <div 
                  key={platform.name}
                  className="platform-node"
                  style={{
                    position: 'absolute',
                    top: '50%',
                    left: '50%',
                    width: '65px',
                    height: '65px',
                    margin: '-32.5px 0 0 -32.5px',
                    animation: `orbit 22s linear infinite`,
                    animationDelay: `-${22 * (index / Math.max(socialPlatforms.length, 1))}s`
                  }}
                >
                  <a href={`https://${platform.name.toLowerCase()}.com`} target="_blank" rel="noopener noreferrer">
                    <div className="platform-icon" style={{
                      width: '65px',
                      height: '65px',
                      borderRadius: '50%',
                      background: 'var(--bg-primary)',
                      display: 'grid',
                      placeItems: 'center',
                      border: '2px solid var(--border-color)',
                      boxShadow: 'var(--shadow-subtle)',
                      transition: 'all .25s ease',
                      position: 'relative',
                      zIndex: 3
                    }}>
                      <div style={{
                        width: '34px',
                        height: '34px',
                        borderRadius: '50%',
                        background: 'var(--bg-tertiary)',
                        display: 'grid',
                        placeItems: 'center',
                        color: 'white',
                        fontWeight: 'bold',
                        fontSize: '0.8rem'
                      }}>
                        {platform.name.charAt(0)}
                      </div>
                    </div>
                  </a>
                  <div className="platform-name" style={{
                    position: 'absolute',
                    bottom: '-34px',
                    left: '50%',
                    transform: 'translateX(-50%)',
                    fontSize: '.85rem',
                    fontWeight: 700,
                    whiteSpace: 'nowrap',
                    background: 'var(--bg-primary)',
                    border: '1px solid var(--border-color)',
                    color: 'var(--text-primary)',
                    padding: '.35rem .75rem',
                    borderRadius: '999px',
                    boxShadow: 'var(--shadow-subtle)'
                  }}>
                    {platform.name}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>

      <footer className="footer">
        <div className="footer-content">
          <div className="footer-section">
            <h4>Posting Expert</h4>
            <p>AI-Powered Social Media Automation</p>
            <p className="footer-tagline">Built by Inikola Ã— CraftingBrain</p>
          </div>
          <div className="footer-section">
            <h4>Quick Links</h4>
            <Link to="/login">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/privacy-policy">Privacy Policy</Link>
            <Link to="/contact-us">Contact Us</Link>
          </div>
          <div className="footer-section">
            <h4>Connect</h4>
            <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">LinkedIn</a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">Instagram</a>
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">Facebook</a>
          </div>
        </div>
        <div className="footer-bottom">
          <p>Â© 2025 Posting Expert. All rights reserved. | Powered by Inikola Technologies & CraftingBrain</p>
        </div>
      </footer>
    </div>
  );
};

export default Register;
