// src/components/Login.js - UPDATED WITH ENHANCED NAVBAR
import React, { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [typedText, setTypedText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);
  const navigate = useNavigate();

  const phrases = useMemo(() => [
    'Generate AI Content',
    'Post to Multiple Platforms',
    'Smart Scheduling',
    'Boost Engagement',
    'Automate Marketing'
  ], []);

  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting
          ? fullText.substring(0, typedText.length - 1)
          : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 2000);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
    
    if (newMode) {
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
    }
  };

  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark" || !saved) {
      setIsDarkMode(true);
      document.body.classList.add("dark");
    }

    const rememberedUsername = localStorage.getItem("rememberedUsername");
    const rememberedRememberMe = localStorage.getItem("rememberMe") === "true";
    
    if (rememberedUsername && rememberedRememberMe) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }

    const token = localStorage.getItem("token");
    if (token) {
      const tokenExpiry = localStorage.getItem("tokenExpiry");
      if (tokenExpiry && parseInt(tokenExpiry) > Date.now()) {
        navigate("/connect", { replace: true });
      } else {
        localStorage.removeItem("token");
        localStorage.removeItem("tokenExpiry");
      }
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);
    
    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password, rememberMe }
      );
      
      const { token, user, rememberMe: rememberMeResponse, expiresIn } = response.data;
      
      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);
      
      const expiryTime = Date.now() + (expiresIn * 1000);
      localStorage.setItem("tokenExpiry", expiryTime.toString());
      
      if (rememberMeResponse) {
        localStorage.setItem("rememberedUsername", username);
        localStorage.setItem("rememberMe", "true");
      } else {
        localStorage.removeItem("rememberedUsername");
        localStorage.removeItem("rememberMe");
      }
      
      setSuccess("Login successful! Redirecting...");
      
      if (onLogin) {
        onLogin(user);
      }
      
      setTimeout(() => {
        setIsLoading(false);
        navigate("/connect", { replace: true });
      }, 1000);
      
    } catch (err) {
      const errorMessage = err.response?.data?.error || "Invalid username or password";
      setError(errorMessage);
      setIsLoading(false);
    }
  };

  const socialPlatforms = [
    { name: 'LinkedIn', icon: '/images/linkedin.png', color: '#0077B5' },
    { name: 'Instagram', icon: '/images/instagram.jpg', color: '#E1306C' },
    { name: 'Facebook', icon: '/images/facebook.png', color: '#1877F2' }
  ];

  const trustedCompanies = [
    { name: 'Inikola', logo: 'ðŸ¢', description: 'Tech Innovation' },
    { name: 'CraftingBrain', logo: 'ðŸ§ ', description: 'EdTech Company' }
  ];

  const stats = [
    // { value: '10K+', label: 'Posts Generated', icon: 'ðŸ“' },
    // { value: '500+', label: 'Active Users', icon: 'ðŸ‘¥' },
    // { value: '98%', label: 'Success Rate', icon: 'âœ¨' },
    // { value: '24/7', label: 'Support', icon: 'ðŸš€' }
  ];

  return (
    <div className="futuristic-login-container">
      <div className="cyber-grid"></div>
      
      <div className="geometric-bg">
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
      </div>
          
      <div className="floating-particles">
        {[...Array(20)].map((_, i) => (
          <div key={i} className="particle"></div>
        ))}
      </div>

      {/* Enhanced Navbar */}
      <nav className="premium-navbar">
        <div className="nav-brand">
          <img src="/123.png" alt="Marketing Bot" className="nav-logo" />
          <span className="nav-brand-text">Marketing Bot</span>
        </div>
        
        <div className="nav-links">
          <Link to="/login" className="nav-link active">Home</Link>
          <Link to="/about" className="nav-link">About</Link>
          <Link to="/privacy-policy" className="nav-link">Privacy Policy</Link>
          <Link to="/contact-us" className="nav-link">Contact Us</Link>
        </div>

        <button className="theme-toggle-futuristic nav-theme-toggle" onClick={toggleTheme}>
          {isDarkMode ? 'â˜€ï¸' : 'ðŸŒ™'}
        </button>
      </nav>

      <div id="home" className="login-content-wrapper">
        {/* Rest of your existing Login.js content remains exactly the same */}
        <div className="hero-section">
          {/* Premium Header Section */}
          <div className="brand-header-futuristic">
            <div className="brand-presents">Powered by Inikola Technologies</div>
            <div className="brand-main">
              <div className="logo-container">
                <img src="/123.png" alt="Marketing Bot" className="brand-logo-futuristic" />
                <div className="logo-glow"></div>
              </div>
              <div className="brand-info-futuristic">
                <h1>Marketing Bot</h1>
                <p className="brand-tagline">AI-Powered Social Media Automation</p>
              </div>
            </div>
          </div>

          {/* Hero Typing Section */}
          <div className="hero-typing-container">
            <h2 className="hero-title">
              <span className="gradient-text">{typedText}</span>
              <span className="cursor-blink">|</span>
            </h2>
            <p className="hero-subtitle">
              Transform your social media strategy with cutting-edge AI technology. 
              Generate stunning content, schedule posts, and grow your presence across multiple platforms.
            </p>
          </div>

          {/* Stats Overview */}
          <div className="stats-overview">
            {stats.map((stat, index) => (
              <div key={index} className="stat-card">
                <div className="stat-icon">{stat.icon}</div>
                <div className="stat-value">{stat.value}</div>
                <div className="stat-label">{stat.label}</div>
              </div>
            ))}
          </div>

          {/* Trusted By Section */}
          <div id="trusted" className="trusted-section">
            <h3 className="section-title">
              <span className="icon-pulse">ðŸ†</span> Trusted By Leading Companies
            </h3>
            <p className="section-subtitle">Join innovative teams already using Marketing Bot</p>
            
            <div className="trusted-companies">
              {trustedCompanies.map((company, index) => (
                <div key={index} className="company-card">
                  <div className="company-logo">{company.logo}</div>
                  <div className="company-info">
                    <h4>{company.name}</h4>
                    <p>{company.description}</p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Integrations Section */}
          <div className="integrations-section">
            <h3 className="section-title">
              <span className="icon-pulse">ðŸ”—</span> Seamless Integrations
            </h3>
            <p className="section-subtitle">Connect with your favorite platforms instantly</p>
            
            <div className="platforms-orbit">
              <div className="orbit-center">
                <div className="center-logo">ðŸ¤–</div>
                <div className="pulse-ring"></div>
                <div className="pulse-ring-2"></div>
              </div>
              
              {socialPlatforms.map((platform, index) => (
                <div 
                  key={platform.name}
                  className="platform-node"
                  style={{
                    '--index': index,
                    '--total': socialPlatforms.length,
                    '--color': platform.color
                  }}
                >
                  <a href={`https://${platform.name.toLowerCase()}.com`} target="_blank" rel="noopener noreferrer">
                    <div className="platform-icon">
                      <img src={platform.icon} alt={platform.name} />
                    </div>
                  </a>
