import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './ContactUs.css';

const ContactUs = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [formStatus, setFormStatus] = useState({ type: '', message: '' });

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'dark') {
      setIsDarkMode(true);
      document.body.classList.add('dark');
    }
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    
    if (newMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  };

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Simulate form submission
    setFormStatus({ type: 'loading', message: 'Sending your message...' });
    
    setTimeout(() => {
      setFormStatus({
        type: 'success',
        message: 'Thank you for reaching out! We\'ll get back to you within 24 hours.'
      });
      setFormData({ name: '', email: '', subject: '', message: '' });
    }, 1500);
  };

  return (
    <div className={`contact-container ${isDarkMode ? 'dark' : 'light'}`}>
      {/* Animated Background */}
      <div className="animated-bg">
        <div className="ball ball-1"></div>
        <div className="ball ball-2"></div>
        <div className="ball ball-3"></div>
      </div>

      {/* Navigation */}
      <nav className="contact-nav">
        <div className="nav-content">
          <Link to="/" className="nav-logo">
            <img src="/logo.png" alt="CraftingBrain" className="logo-img" />
            <span className="logo-text">PostingExpert</span>
          </Link>
          
          <button className="mobile-menu-toggle" onClick={() => document.querySelector('.nav-links').classList.toggle('active')}>
            ‚ò∞
          </button>
          
          <div className="nav-links">
            <Link to="/" className="nav-link">Home</Link>
            <Link to="/about" className="nav-link">About Us</Link>
            <Link to="/contact" className="nav-link active">Contact</Link>
            <Link to="/privacy" className="nav-link">Privacy</Link>
            <Link to="/login" className="nav-link nav-link-cta">Login</Link>
          </div>
          
          <button className="theme-toggle-nav" onClick={toggleTheme}>
            {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>
      </nav>

      {/* Hero Section */}
      <section className="contact-hero">
        <h1 className="contact-hero-title">
          Get in <span className="gradient-text">Touch</span>
        </h1>
        <p className="contact-hero-subtitle">
          We'd love to hear from you. Send us a message and we'll respond as soon as possible.
        </p>
      </section>

      {/* Contact Content */}
      <section className="contact-content">
        <div className="contact-wrapper">
          {/* Contact Form */}
          <div className="contact-form-section">
            <h2 className="form-title">Send Us a Message</h2>
            <form className="contact-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="name">Full Name</label>
                <input
                  type="text"
                  id="name"
                  name="name"
                  value={formData.name}
                  onChange={handleChange}
                  placeholder="John Doe"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="email">Email Address</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleChange}
                  placeholder="john@example.com"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="subject">Subject</label>
                <input
                  type="text"
                  id="subject"
                  name="subject"
                  value={formData.subject}
                  onChange={handleChange}
                  placeholder="How can we help?"
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="message">Message</label>
                <textarea
                  id="message"
                  name="message"
                  value={formData.message}
                  onChange={handleChange}
                  placeholder="Tell us more about your inquiry..."
                  rows="6"
                  required
                ></textarea>
              </div>

              <button type="submit" className="submit-button">
                {formStatus.type === 'loading' ? 'Sending...' : 'Send Message'}
              </button>

              {formStatus.message && (
                <div className={`form-status ${formStatus.type}`}>
                  {formStatus.message}
                </div>
              )}
            </form>
          </div>

          {/* Contact Info */}
          <div className="contact-info-section">
            <h2 className="info-title">Contact Information</h2>
            
            <div className="info-card">
              <div className="info-icon">üìß</div>
              <div className="info-content">
                <h3>Email</h3>
                <p>contact@postingexpert.com</p>
                <p>support@postingexpert.com</p>
              </div>
            </div>

            <div className="info-card">
              <div className="info-icon">üì±</div>
              <div className="info-content">
                <h3>Phone</h3>
                <p>+91 9115706096</p>
                <p>Mon-Fri, 9am-6pm IST</p>
              </div>
            </div>

            <div className="info-card">
              <div className="info-icon">üè¢</div>
              <div className="info-content">
                <h3>Office</h3>
                <p>CraftingBrain Technologies</p>
                <p>Hyderabad, Telangana, India</p>
              </div>
            </div>

            <div className="info-card">
              <div className="info-icon">‚è∞</div>
              <div className="info-content">
                <h3>Business Hours</h3>
                <p>Monday - Friday: 9:00 AM - 6:00 PM</p>
                <p>Saturday: 10:00 AM - 4:00 PM</p>
              </div>
            </div>

            <div className="social-links">
              <h3>Follow Us</h3>
              <div className="social-icons">
                <a href="#" className="social-icon">üîó</a>
                <a href="#" className="social-icon">ùïè</a>
                <a href="#" className="social-icon">üìò</a>
                <a href="#" className="social-icon">üì∏</a>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* FAQ Section */}
      <section className="faq-section">
        <h2 className="section-title">Frequently Asked Questions</h2>
        <div className="faq-grid">
          <div className="faq-card">
            <h3>How quickly will I get a response?</h3>
            <p>We typically respond to all inquiries within 24 hours during business days.</p>
          </div>
          <div className="faq-card">
            <h3>Do you offer phone support?</h3>
            <p>Yes, phone support is available Monday-Friday from 9am-6pm IST.</p>
          </div>
          <div className="faq-card">
            <h3>Can I schedule a demo?</h3>
            <p>Absolutely! Contact us to schedule a personalized demo of PostingExpert.</p>
          </div>
          <div className="faq-card">
            <h3>What about technical support?</h3>
            <p>Technical support is available 24/7 through our support email.</p>
          </div>
        </div>
      </section>

      {/* Footer */}
      <footer className="contact-footer">
        <div className="footer-content">
          <div className="footer-section">
            <h4>PostingExpert</h4>
            <p>AI-Powered Social Media Automation</p>
          </div>
          <div className="footer-section">
            <h4>Quick Links</h4>
            <Link to="/">Home</Link>
            <Link to="/about">About Us</Link>
            <Link to="/privacy">Privacy Policy</Link>
          </div>
          <div className="footer-section">
            <h4>Contact</h4>
            <p>Email: contact@postingexpert.com</p>
            <p>Phone: +91 9115706096</p>
          </div>
        </div>
        <div className="footer-bottom">
          <p>¬© 2025 PostingExpert by CraftingBrain. All rights reserved.</p>
        </div>
      </footer>
    </div>
  );
};

export default ContactUs;
