// src/Connect.js - Futuristic AI Version
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Connect.css';
import LinkedInConnect from './components/LinkedInConnect';
import InstagramConnect from './components/InstagramConnect';
import FacebookConnect from './components/FacebookConnect';

const Connect = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [user, setUser] = useState(null);
  const [showProfileMenu, setShowProfileMenu] = useState(false);
  const [socialConnections, setSocialConnections] = useState({
    instagram: { connected: false, detail: null },
    linkedin: { connected: false, detail: null },
    twitter: { connected: false, detail: null },
    facebook: { connected: false, detail: null }
  });
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem('theme');
    if (saved === 'dark' || !saved) {
      setIsDarkMode(true);
      document.body.classList.add('dark');
    }

    const token = localStorage.getItem('token');
    const username = localStorage.getItem('username');
    
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchUserData = async () => {
      try {
        const response = await fetch(
          `http://13.233.45.167:5000/user/profile`,
          {
            headers: {
              'Authorization': `Bearer ${token}`,
              'Content-Type': 'application/json'
            }
          }
        );

        if (response.ok) {
          const userData = await response.json();
          console.log('User data from API:', userData);
          
          setUser({
            username: userData.username || username,
            email: userData.email || 'user@example.com',
            businessType: userData.business_type || 'Not specified',
            joinDate: userData.created_at 
              ? new Date(userData.created_at * 1000).toLocaleDateString('en-US', { month: 'long', year: 'numeric' })
              : 'Recently',
            postsCreated: userData.posts_created || 0,
            connectedAccounts: userData.connected_accounts || 0
          });
        } else {
          setUser({
            username: username || 'User',
            email: 'user@example.com',
            businessType: 'Not specified',
            joinDate: 'Recently',
            postsCreated: 0,
            connectedAccounts: 0
          });
        }
      } catch (error) {
        console.error('Error fetching user data:', error);
        setUser({
          username: username || 'User',
          email: 'user@example.com',
          businessType: 'Not specified',
          joinDate: 'Recently',
          postsCreated: 0,
          connectedAccounts: 0
        });
      }
    };

    fetchUserData();
    fetchSocialStatus();
  }, [navigate]);

  const fetchSocialStatus = async () => {
    try {
      const token = localStorage.getItem('token');
      const appUser = localStorage.getItem('username');
      
      const response = await fetch(
        `http://13.233.45.167:5000/social/status?app_user=${appUser}`,
        {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        }
      );

      if (response.ok) {
        const data = await response.json();
        console.log('Social status data:', data);
        
        setSocialConnections({
          instagram: {
            connected: data.instagram?.connected || false,
            detail: data.instagram?.detail || null
          },
          linkedin: {
            connected: data.linkedin?.connected || false,
            detail: data.linkedin?.detail || null
          },
          twitter: {
            connected: data.twitter?.connected || false,
            detail: data.twitter?.detail || null
          },
          facebook: {
            connected: data.facebook?.connected || false,
            detail: data.facebook?.detail || null
          }
        });
      }
    } catch (error) {
      console.error('Error fetching social status:', error);
    }
  };

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    
    if (newMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  };

  const handleLogout = () => {
    setShowProfileMenu(false);
    localStorage.removeItem('token');
    localStorage.removeItem('username');
    navigate('/login', { replace: true });
  };

  const handleSocialConnect = (platform) => {
    if (platform === 'Twitter') {
      alert(`${platform} connection coming soon!`);
    }
  };

  const connectedAccounts = [
    { 
      name: 'Instagram', 
      icon: 'üì∑', 
      connected: socialConnections.instagram.connected, 
      color: '#E1306C',
      component: InstagramConnect,
      detail: socialConnections.instagram.detail
    },
    { 
      name: 'LinkedIn', 
      icon: 'üíº', 
      connected: socialConnections.linkedin.connected, 
      color: '#0077B5',
      component: LinkedInConnect,
      detail: socialConnections.linkedin.detail
    },
    { 
      name: 'Twitter', 
      icon: 'üê¶', 
      connected: socialConnections.twitter.connected, 
      color: '#1DA1F2',
      component: null,
      detail: socialConnections.twitter.detail
    },
    { 
      name: 'Facebook', 
      icon: 'üë•', 
      connected: socialConnections.facebook.connected, 
      color: '#1877F2',
      component: FacebookConnect,
      detail: socialConnections.facebook.detail
    }
  ];

  const quickActions = [
    { title: 'Create Content', icon: '‚ú®', path: '/content-creation', desc: 'Generate AI-powered posts' },
    { title: 'Schedule Posts', icon: 'üìÖ', path: '/schedule', desc: 'Plan your content calendar' },
    { title: 'Analytics', icon: 'üìä', path: '/analytics', desc: 'Track your performance' },
    { title: 'My Posts', icon: 'üìù', path: '/my-posts', desc: 'View all your content' }
  ];

  return (
    <div className="futuristic-connect-container">
      <div className="cyber-grid"></div>
      <div className="floating-particles">
        {[...Array(20)].map((_, i) => (
          <div key={i} className="particle"></div>
        ))}
      </div>

      <button className="theme-toggle-futuristic" onClick={toggleTheme}>
        {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
      </button>

      <div className="header-nav">
        <div className="nav-link active">
          <img src="/123.png" alt="Logo" className="header-logo-futuristic" />
          <span>Marketing Bot</span>
        </div>
        <div className="profile-dropdown-futuristic">
          <button 
            className="profile-btn-futuristic" 
            onClick={() => setShowProfileMenu(!showProfileMenu)}
          >
            <div className="profile-avatar-futuristic">
              {user?.username.charAt(0).toUpperCase()}
            </div>
            <span className="profile-name-futuristic">{user?.username}</span>
            <span className="dropdown-arrow-futuristic">‚ñº</span>
          </button>
          
          {showProfileMenu && (
            <div className="profile-menu-futuristic">
              <div className="profile-menu-header-futuristic">
                <div className="profile-avatar-large-futuristic">
                  {user?.username.charAt(0).toUpperCase()}
                </div>
                <div className="profile-info-futuristic">
                  <h3>{user?.username}</h3>
                  <p>{user?.email}</p>
                </div>
              </div>
              <div className="profile-menu-divider-futuristic"></div>
              <button className="profile-menu-item-futuristic" onClick={() => navigate('/profile')}>
                üë§ View Profile
              </button>
              <button className="profile-menu-item-futuristic" onClick={() => navigate('/settings')}>
                ‚öôÔ∏è Settings
              </button>
              <div className="profile-menu-divider-futuristic"></div>
              <button className="profile-menu-item-futuristic logout-futuristic" onClick={handleLogout}>
                üö™ Logout
              </button>
            </div>
          )}
        </div>
      </div>

      <div className="futuristic-connect-content">
        
        <section className="welcome-section-futuristic">
          <h2 className="section-title-futuristic">Welcome back, {user?.username}! üëã</h2>
          <p className="section-subtitle-futuristic">
            Let's create amazing content for your {user?.businessType} business
          </p>
        </section>

        <section className="quick-actions-section-futuristic">
          <h3 className="section-heading-futuristic">Quick Actions</h3>
          <div className="quick-actions-grid-futuristic">
            {quickActions.map((action, index) => (
              <button
                key={index}
                className="action-card-futuristic"
                onClick={() => navigate(action.path)}
              >
                <div className="action-icon-futuristic">{action.icon}</div>
                <h4 className="action-title-futuristic">{action.title}</h4>
                <p className="action-desc-futuristic">{action.desc}</p>
              </button>
            ))}
          </div>
        </section>

        <section className="social-connect-section-futuristic">
          <div className="section-header-futuristic">
            <h3 className="section-heading-futuristic">Connect Social Media</h3>
            <p className="section-description-futuristic">
              Link your accounts to start posting automatically
            </p>
          </div>
          
          <div className="social-accounts-grid-futuristic">
            {connectedAccounts.map((account, index) => (
              <div key={index} className="social-account-card-futuristic">
                <div className="social-icon-futuristic" style={{ background: account.color }}>
                  {account.icon}
                </div>
                <div className="social-info-futuristic">
                  <h4 className="social-name-futuristic">{account.name}</h4>
                  <p className="social-status-futuristic">
                    {account.connected ? 'Connected' : 'Not connected'}
                  </p>
                  {account.name === 'Facebook' && account.connected && account.detail && (
                    <p className="social-detail-futuristic">
                      Page: {account.detail.page_name}
                    </p>
                  )}
                </div>
                {account.component ? (
                  <account.component
                    appUser={user?.username}
                    onConnected={fetchSocialStatus}
                    connected={account.connected}
                    status={account.detail}
                  />
                ) : (
                  <button 
                    className={`social-connect-btn-futuristic ${account.connected ? 'connected' : ''}`}
                    onClick={() => handleSocialConnect(account.name)}
                  >
                    {account.connected ? 'Disconnect' : 'Connect'}
                  </button>
                )}
              </div>
            ))}
          </div>
        </section>

        <section className="profile-card-section-futuristic">
          <div className="profile-card-futuristic">
            <div className="profile-card-header-futuristic">
              <div className="profile-avatar-xl-futuristic">
                {user?.username.charAt(0).toUpperCase()}
              </div>
              <div className="profile-card-info-futuristic">
                <h3 className="profile-card-name-futuristic">{user?.username}</h3>
                <p className="profile-card-email-futuristic">{user?.email}</p>
              </div>
              <button 
                className="edit-profile-btn-futuristic"
                onClick={() => navigate('/profile')}
              >
                Edit Profile
              </button>
            </div>
            
            <div className="profile-card-divider-futuristic"></div>
            
            <div className="profile-card-details-futuristic">
              <div className="profile-detail-item-futuristic">
                <span className="detail-label-futuristic">Business Type</span>
                <span className="detail-value-futuristic">{user?.businessType || 'Not specified'}</span>
              </div>
              <div className="profile-detail-item-futuristic">
                <span className="detail-label-futuristic">Member Since</span>
                <span className="detail-value-futuristic">{user?.joinDate}</span>
              </div>
              <div className="profile-detail-item-futuristic">
                <span className="detail-label-futuristic">Posts Created</span>
                <span className="detail-value-futuristic">{user?.postsCreated || 0}</span>
              </div>
              <div className="profile-detail-item-futuristic">
                <span className="detail-label-futuristic">Accounts Connected</span>
                <span className="detail-value-futuristic">
                  {Object.values(socialConnections).filter(s => s.connected).length}/4
                </span>
              </div>
            </div>
          </div>
        </section>

      </div>
    </div>
  );
};

export default Connect;
