// src/components/Register.js - Futuristic AI Version
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
    confirmPassword: ''
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState(0);
  
  // Password visibility states
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  
  const navigate = useNavigate();

  useEffect(() => {
    const saved = localStorage.getItem('theme');
    if (saved === 'dark' || !saved) {
      setIsDarkMode(true);
      document.body.classList.add('dark');
    }

    const token = localStorage.getItem('token');
    if (token) {
      const tokenExpiry = localStorage.getItem('tokenExpiry');
      if (tokenExpiry && parseInt(tokenExpiry) > Date.now()) {
        navigate('/connect', { replace: true });
      } else {
        localStorage.removeItem('token');
        localStorage.removeItem('tokenExpiry');
      }
    }
  }, [navigate]);

  useEffect(() => {
    if (formData.password) {
      calculatePasswordStrength(formData.password);
    }
  }, [formData.password]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    
    if (newMode) {
      document.body.classList.add('dark');
    } else {
      document.body.classList.remove('dark');
    }
  };

  const calculatePasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 6) strength++;
    if (password.length >= 10) strength++;
    if (/[a-z]/.test(password) && /[A-Z]/.test(password)) strength++;
    if (/\d/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;
    setPasswordStrength(strength);
  };

  const getPasswordStrengthLabel = () => {
    if (passwordStrength === 0) return '';
    if (passwordStrength <= 2) return 'Weak';
    if (passwordStrength <= 3) return 'Fair';
    if (passwordStrength <= 4) return 'Good';
    return 'Strong';
  };

  const getPasswordStrengthColor = () => {
    if (passwordStrength <= 2) return '#ef4444';
    if (passwordStrength <= 3) return '#f59e0b';
    if (passwordStrength <= 4) return '#10b981';
    return '#22d3ee';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
    
    if (error) setError('');
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = 'Name is required';
    } else if (formData.name.trim().length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      errors.email = 'Please enter a valid email address';
    }

    if (!formData.username.trim()) {
      errors.username = 'Username is required';
    } else if (formData.username.trim().length < 3) {
      errors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      errors.username = 'Username can only contain letters, numbers, and underscores';
    }

    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    return errors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }

    setValidationErrors({});
    setIsLoading(true);

    try {
      const response = await fetch(
        'https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/register',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: formData.name.trim(),
            email: formData.email.toLowerCase().trim(),
            username: formData.username.trim(),
            password: formData.password,
            confirmPassword: formData.confirmPassword 
          }),
        }
      );

      const data = await response.json();
      console.log('Registration response:', { status: response.status, data });

      if (response.ok) {
        setSuccess('Registration successful! Taking you to the survey...');
        
        localStorage.setItem('registeredUserId', formData.username.trim());
        localStorage.setItem('username', formData.username.trim());
        
        if (data.token) {
          localStorage.setItem('token', data.token);
        }
        
        console.log('Saved to localStorage:', {
          registeredUserId: formData.username.trim(),
          username: formData.username.trim()
        });
        
        setFormData({
          name: '',
          email: '',
          username: '',
          password: '',
          confirmPassword: ''
        });
        
        setTimeout(() => {
          navigate('/survey');
        }, 1500);

      } else {
        if (data.error) {
          const errorMessage = data.error.toLowerCase();
          
          if (errorMessage.includes('username') && (errorMessage.includes('already') || errorMessage.includes('exists'))) {
            setValidationErrors({ 
              username: 'Username already taken. Please choose a different username.' 
            });
            setError('');
          } 
          else if (errorMessage.includes('email') && (errorMessage.includes('already') || errorMessage.includes('registered'))) {
            setValidationErrors({ 
              email: 'This email is already registered. Please use a different email or try logging in.' 
            });
            setError('');
          } 
          else if (errorMessage.includes('user') && errorMessage.includes('already')) {
            setError('An account with these details already exists. Please try logging in instead.');
          }
          else if (errorMessage.includes('password') && errorMessage.includes('match')) {
            setValidationErrors({
              confirmPassword: 'Passwords do not match'
            });
            setError('');
          }
          else if (errorMessage.includes('invalid') && errorMessage.includes('email')) {
            setValidationErrors({
              email: 'Please enter a valid email address'
            });
            setError('');
          }
          else if (errorMessage.includes('invalid') && errorMessage.includes('username')) {
            setValidationErrors({
              username: 'Username can only contain letters, numbers, and underscores'
            });
            setError('');
          }
          else {
            setError(data.error);
          }
        } 
        else {
          if (response.status === 409) {
            setError('User already exists. Please try logging in or use different credentials.');
          } else if (response.status === 400) {
            setError('Invalid registration data. Please check all fields and try again.');
          } else if (response.status === 500) {
            setError('Server error occurred. Please try again later.');
          } else {
            setError('Registration failed. Please try again.');
          }
        }
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError('Network error. Please check your connection and try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="futuristic-register-container">
      <div className="cyber-grid"></div>
      <div className="floating-particles">
        {[...Array(20)].map((_, i) => (
          <div key={i} className="particle"></div>
        ))}
      </div>

      <button className="theme-toggle-futuristic" onClick={toggleTheme}>
        {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
      </button>

      <button className="logout-button-futuristic" onClick={() => navigate('/login')}>
        Logout ‚Üí
      </button>

      <div className="extra-icons">
        <span className="icon1">üöÄ</span>
        <span className="icon2">ü§ñ</span>
        <span className="icon3">üìä</span>
        <span className="icon4">üí°</span>
      </div>

      <div className="futuristic-split-content">
        {/* Left Side - Marketing Message */}
        <div className="marketing-section-futuristic">
          <div className="marketing-content-futuristic">
            <div className="brand-section-futuristic">
              <img src="/123.png" alt="Marketing Bot Logo" className="marketing-logo-futuristic" />
            </div>
            
            <h1 className="marketing-title-futuristic">
              START YOUR
              <span className="marketing-highlight-futuristic">MARKETING REVOLUTION</span>
              Join the AI-Powered Future
            </h1>
            
            <p className="marketing-description-futuristic">
              Transform your business with the power of AI. Join 1000+ forward-thinking entrepreneurs 
              who are already dominating social media. One platform, unlimited possibilities ‚Äì create, 
              schedule, and scale your brand like never before.
            </p>

            <div className="marketing-stats-futuristic">
              <div className="stat-item-futuristic">
                <div className="stat-number-futuristic">10+</div>
                <div className="stat-label-futuristic">Active Users</div>
              </div>
              <div className="stat-item-futuristic">
                <div className="stat-number-futuristic">50+</div>
                <div className="stat-label-futuristic">Posts</div>
              </div>
              <div className="stat-item-futuristic">
                <div className="stat-number-futuristic">98%</div>
                <div className="stat-label-futuristic">Success</div>
              </div>
            </div>

            <div className="marketing-benefits-futuristic">
              <div className="benefit-item-futuristic">
                <div className="benefit-icon-futuristic">üé®</div>
                <div className="benefit-text-futuristic">AI Content Generation</div>
              </div>
              <div className="benefit-item-futuristic">
                <div className="benefit-icon-futuristic">üìä</div>
                <div className="benefit-text-futuristic">Real-time Analytics</div>
              </div>
              <div className="benefit-item-futuristic">
                <div className="benefit-icon-futuristic">‚ö°</div>
                <div className="benefit-text-futuristic">Smart Automation</div>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Registration Form */}
        <div className="register-section-futuristic">
          <div className="register-form-container-futuristic">
            <h1 className="register-form-title-futuristic">Create Your Account</h1>
            <p className="register-form-subtitle-futuristic">Start your free journey today</p>

            <div className="info-card-futuristic">
              <span style={{ fontSize: '1.25rem' }}>‚ÑπÔ∏è</span>
              <p style={{ fontSize: '0.875rem', margin: 0, lineHeight: 1.6 }}>
                After registration, complete a quick survey to personalize your content.
              </p>
            </div>

            <form onSubmit={handleSubmit}>
              <div className="form-group-futuristic">
                <label>Full Name *</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter your full name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={validationErrors.name ? 'error' : ''}
                  required
                />
                {validationErrors.name && <span className="field-error-futuristic">{validationErrors.name}</span>}
              </div>

              <div className="form-group-futuristic">
                <label>Email Address *</label>
                <input
                  type="email"
                  name="email"
                  placeholder="your.email@example.com"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={validationErrors.email ? 'error' : ''}
                  required
                />
                {validationErrors.email && <span className="field-error-futuristic">{validationErrors.email}</span>}
              </div>

              <div className="form-group-futuristic">
                <label>Username *</label>
                <input
                  type="text"
                  name="username"
                  placeholder="Choose a unique username"
                  value={formData.username}
                  onChange={handleInputChange}
                  className={validationErrors.username ? 'error' : ''}
                  required
                />
                {validationErrors.username && <span className="field-error-futuristic">{validationErrors.username}</span>}
                <small className="field-hint-futuristic">Letters, numbers, and underscores only. Min 3 characters.</small>
              </div>

              <div className="form-group-futuristic">
                <label>Password *</label>
                <div className="password-input-wrapper-futuristic">
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    placeholder="Create a strong password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className={validationErrors.password ? 'error' : ''}
                    required
                  />
                  <button
                    type="button"
                    className="password-toggle-btn-futuristic"
                    onClick={() => setShowPassword(!showPassword)}
                    aria-label={showPassword ? "Hide password" : "Show password"}
                  >
                    {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                  </button>
                </div>
                {formData.password && (
                  <div style={{
                    marginTop: '0.5rem',
                    display: 'flex',
                    alignItems: 'center',
                    gap: '0.5rem'
                  }}>
                    <div style={{
                      flex: 1,
                      height: '4px',
                      background: 'rgba(30, 41, 59, 0.5)',
                      borderRadius: '2px',
                      overflow: 'hidden'
                    }}>
                      <div style={{
                        height: '100%',
                        width: `${(passwordStrength / 5) * 100}%`,
                        background: getPasswordStrengthColor(),
                        transition: 'all 0.3s ease'
                      }}></div>
                    </div>
                    <span style={{
                      fontSize: '0.75rem',
                      color: getPasswordStrengthColor(),
                      fontWeight: 600
                    }}>{getPasswordStrengthLabel()}</span>
                  </div>
                )}
                {validationErrors.password && <span className="field-error-futuristic">{validationErrors.password}</span>}
                <small className="field-hint-futuristic">Use uppercase, lowercase, numbers & symbols for better security.</small>
              </div>

              <div className="form-group-futuristic">
                <label>Confirm Password *</label>
                <div className="password-input-wrapper-futuristic">
                  <input
                    type={showConfirmPassword ? "text" : "password"}
                    name="confirmPassword"
                    placeholder="Confirm your password"
                    value={formData.confirmPassword}
                    onChange={handleInputChange}
                    className={validationErrors.confirmPassword ? 'error' : ''}
                    required
                  />
                  <button
                    type="button"
                    className="password-toggle-btn-futuristic"
                    onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                    aria-label={showConfirmPassword ? "Hide password" : "Show password"}
                  >
                    {showConfirmPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                  </button>
                </div>
                {validationErrors.confirmPassword && <span className="field-error-futuristic">{validationErrors.confirmPassword}</span>}
              </div>

              {error && (
                <div className="response-error-futuristic">
                  <div className="response-icon-futuristic">!</div>
                  <div className="response-content">
                    <p>{error}</p>
                  </div>
                </div>
              )}
              
              {success && (
                <div className="response-success-futuristic">
                  <div className="response-icon-futuristic">‚úì</div>
                  <div className="response-content">
                    <p>{success}</p>
                  </div>
                </div>
              )}

              <button type="submit" className="post-button-futuristic" disabled={isLoading}>
                {isLoading ? <span className="spinner-futuristic"></span> : 'Create Account'}
              </button>
            </form>

            <p style={{ 
              marginTop: '1.5rem', 
              textAlign: 'center', 
              fontSize: '0.875rem',
              color: 'var(--text-secondary)'
            }}>
              Already have an account?{' '}
              <a 
                href="/login" 
                style={{ 
                  color: 'var(--text-primary)',
                  fontWeight: 'bold', 
                  textDecoration: 'none',
                  transition: 'all 0.3s ease'
                }}
                onMouseEnter={(e) => e.target.style.color = 'var(--cyber-blue)'}
                onMouseLeave={(e) => e.target.style.color = 'var(--text-primary)'}
              >
                Login here
              </a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
