// src/components/Login.js - Professional Light Theme with Fixed Navbar
import React, { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [typedText, setTypedText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);
  const [showLoginModal, setShowLoginModal] = useState(false);
  const navigate = useNavigate();

  const phrases = useMemo(() => [
    'Browse everything.',
    'Generate AI Content',
    'Post to Multiple Platforms',
    'Smart Scheduling',
    'Boost Engagement'
  ], []);

  const horizontalFeatures = [
    {
      icon: "ðŸ”—",
      title: "Multi-Platform Integration",
      description: "Connect to LinkedIn, Instagram, Facebook, Twitter, and more. Manage all your social media accounts from one dashboard."
    },
    {
      icon: "ðŸ¤–",
      title: "AI Content Creation",
      description: "Automatically generate engaging posts, captions, and stunning images using advanced AI technology."
    },
    {
      icon: "ðŸ“¤",
      title: "Auto Posting",
      description: "Schedule and publish content automatically across all your connected social media platforms with one click."
    },
    {
      icon: "ðŸ“Š",
      title: "Advanced Analytics",
      description: "Track performance with detailed analytics, engagement metrics, and growth insights for all your platforms."
    },
    {
      icon: "ðŸŽ¯",
      title: "Smart Scheduling",
      description: "Optimize posting times for maximum engagement using AI-powered scheduling algorithms."
    },
    {
      icon: "âœ¨",
      title: "Premium Experience",
      description: "Enjoy a seamless, intuitive interface designed for both beginners and marketing professionals."
    }
  ];

  // Typing animation effect
  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting
          ? fullText.substring(0, typedText.length - 1)
          : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 2000);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  const openLoginModal = () => {
    setShowLoginModal(true);
    setError("");
    setSuccess("");
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);
    setError("");
    setSuccess("");
  };

  // Initialize and AUTO-SHOW LOGIN MODAL
  useEffect(() => {
    // Remember me functionality
    const rememberedUsername = localStorage.getItem("rememberedUsername");
    const rememberedRememberMe = localStorage.getItem("rememberMe") === "true";
    
    if (rememberedUsername && rememberedRememberMe) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }

    // Check if user is already logged in
    const token = localStorage.getItem("token");
    if (token) {
      const tokenExpiry = localStorage.getItem("tokenExpiry");
      if (tokenExpiry && parseInt(tokenExpiry) > Date.now()) {
        navigate("/connect", { replace: true });
        return;
      } else {
        localStorage.removeItem("token");
        localStorage.removeItem("tokenExpiry");
      }
    }

    // Auto-show login modal after 1.5 seconds
    const modalTimer = setTimeout(() => {
      setShowLoginModal(true);
    }, 1500);

    return () => clearTimeout(modalTimer);
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);
    
    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password, rememberMe }
      );
      
      const { token, user, rememberMe: rememberMeResponse, expiresIn } = response.data;
      
      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);
      
      const expiryTime = Date.now() + (expiresIn * 1000);
      localStorage.setItem("tokenExpiry", expiryTime.toString());
      
      if (rememberMeResponse) {
        localStorage.setItem("rememberedUsername", username);
        localStorage.setItem("rememberMe", "true");
      } else {
        localStorage.removeItem("rememberedUsername");
        localStorage.removeItem("rememberMe");
      }
      
      setSuccess("Login successful! Redirecting...");
      
      if (onLogin) {
        onLogin(user);
      }
      
      setTimeout(() => {
        setIsLoading(false);
        closeLoginModal();
        navigate("/connect", { replace: true });
      }, 1000);
      
    } catch (err) {
      const errorMessage = err.response?.data?.error || "Invalid username or password";
      setError(errorMessage);
      setIsLoading(false);
    }
  };

  const socialPlatforms = [
    { name: 'LinkedIn', icon: '/images/linkedin.png', color: '#0077B5' },
    { name: 'Instagram', icon: '/images/instagram.jpg', color: '#E1306C' },
    { name: 'Facebook', icon: '/images/facebook.png', color: '#1877F2' }
  ];

  const trustedCompanies = [
    { name: 'Inikola', logo: 'ðŸ¢', description: 'Tech Innovation' },
    { name: 'CraftingBrain', logo: 'ðŸ§ ', description: 'EdTech Company' }
  ];

  return (
    <div className="futuristic-login-container">
      {/* NAVBAR - BULLETPROOF GRID LAYOUT */}
      <nav className="premium-navbar">
        {/* LEFT: Brand */}
        <div className="nav-brand-container">
          <div className="nav-brand">
            <img 
              src="/123.png" 
              alt="Area" 
              className="nav-logo"
              onError={(e) => {
                e.target.style.display = 'none';
              }}
            />
            <span className="nav-brand-text">Area</span>
          </div>
        </div>
        
        {/* CENTER: Navigation Links */}
        <div className="nav-links">
          <a href="#home" className="nav-link active">Benefits</a>
          <Link to="/about" className="nav-link">Specifications</Link>
          <Link to="/privacy-policy" className="nav-link">How-to</Link>
          <Link to="/contact-us" className="nav-link">Contact Us</Link>
        </div>

        {/* RIGHT: Button */}
        <div className="nav-controls">
          <button 
            onClick={openLoginModal}
            type="button"
            className="nav-login-btn"
          >
            Learn More
          </button>
        </div>
      </nav>

      {/* LOGIN MODAL */}
      <div className={`login-modal-overlay ${showLoginModal ? 'active' : ''}`} onClick={closeLoginModal}>
        <div className="login-modal" onClick={(e) => e.stopPropagation()}>
          <button className="login-modal-close" onClick={closeLoginModal} type="button" aria-label="Close modal">
            âœ•
          </button>
          
          <div className="form-header-futuristic">
            <h2>Welcome Back</h2>
            <p>Access your AI marketing dashboard</p>
          </div>

          <form className="futuristic-form" onSubmit={handleSubmit}>
            <div className="input-group-futuristic">
              <label htmlFor="username">USERNAME</label>
              <div className="input-wrapper">
                <input
                  id="username"
                  type="text"
                  placeholder="Enter username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  autoComplete="username"
                  required
                />
              </div>
            </div>

            <div className="input-group-futuristic">
              <label htmlFor="password">PASSWORD</label>
              <div className="input-wrapper password-wrapper-futuristic">
                <input
                  id="password"
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  autoComplete="current-password"
                  required
                />
                <button
                  type="button"
                  className="password-toggle-futuristic"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? "Hide password" : "Show password"}
                >
                  {showPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
                </button>
              </div>
            </div>

            <div className="remember-me-futuristic">
              <label className="checkbox-container">
                <input
                  type="checkbox"
                  checked={rememberMe}
                  onChange={(e) => setRememberMe(e.target.checked)}
                />
                <span className="checkmark"></span>
                <span className="checkbox-label">Remember me for 30 days</span>
              </label>
            </div>

            {error && (
              <div className="alert-futuristic error-alert">
                <span className="alert-icon">âš ï¸</span>
                <span>{error}</span>
              </div>
            )}

            {success && (
              <div className="alert-futuristic success-alert">
                <span className="alert-icon">âœ“</span>
                <span>{success}</span>
              </div>
            )}

            <button 
              type="submit" 
              className="submit-btn-futuristic" 
              disabled={isLoading}
            >
              {isLoading ? (
                <>
                  <span className="loading-spinner"></span>
                  <span>Processing...</span>
                </>
              ) : (
                <>
                  <span>LAUNCH DASHBOARD</span>
                  <span className="btn-arrow">â†’</span>
                </>
              )}
            </button>
          </form>

          <div className="form-footer-futuristic">
            <p>
              New to Posting Expert?{' '}
              <Link to="/register" onClick={closeLoginModal}>Create Account</Link>
            </p>
          </div>
        </div>
      </div>

      {/* HERO SECTION */}
      <div id="home" className="login-content-wrapper">
        <div className="hero-section">
          <div className="brand-header-futuristic">
            <div className="brand-presents">Powered by Inikola Technologies</div>
            <div className="brand-main">
              <div className="logo-container">
                <img 
                  src="/123.png" 
                  alt="Posting Expert" 
                  className="brand-logo-futuristic"
                  onError={(e) => {
                    e.target.style.display = 'none';
                  }}
                />
              </div>
              <div className="brand-info-futuristic">
                <h1>Posting Expert</h1>
                <p className="brand-tagline">AI-Powered Social Media Automation</p>
              </div>
            </div>
          </div>

          <div className="hero-typing-container">
            <h2 className="hero-title">
              <span className="gradient-text">{typedText}</span>
              <span className="cursor-blink">|</span>
            </h2>
            <p className="hero-subtitle">
              Transform your social media strategy with cutting-edge AI technology. 
              Generate stunning content, schedule posts, and grow your presence across multiple platforms.
            </p>
          </div>

          <div className="hero-cta-section">
            <button className="cta-button-futuristic" onClick={openLoginModal} type="button">
              <span>Get Started Now</span>
              <span className="cta-arrow">â†’</span>
            </button>
          </div>

          {/* FEATURES SECTION */}
          <div className="horizontal-features-section">
            <h3 className="section-title">
              <span className="icon-pulse">ðŸš€</span> Powerful Features
            </h3>
            <p className="section-subtitle">Everything you need to automate your social media marketing</p>
            
            <div className="horizontal-features-container">
              <div className="horizontal-features-scroll">
                {horizontalFeatures.map((feature, index) => (
                  <div key={index} className="horizontal-feature-card">
                    <div className="horizontal-feature-icon">
                      {feature.icon}
                    </div>
                    <h4>{feature.title}</h4>
                    <p>{feature.description}</p>
                  </div>
                ))}
              </div>
            </div>
          </div>

          {/* TRUSTED BY SECTION */}
