import React, { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [typedText, setTypedText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);
  const [showLoginModal, setShowLoginModal] = useState(true);
  const navigate = useNavigate();

  const phrases = useMemo(() => [
    'Generate AI Content',
    'Post to Multiple Platforms',
    'Smart Scheduling',
    'Boost Engagement',
    'Automate Marketing'
  ], []);

  const horizontalFeatures = [
    { icon: "üîó", title: "Multi-Platform Integration", description: "Connect to LinkedIn, Instagram, Facebook, Twitter, and more. Manage all your social media accounts from one dashboard." },
    { icon: "ü§ñ", title: "AI Content Creation", description: "Automatically generate engaging posts, captions, and stunning images using advanced AI technology." },
    { icon: "üì§", title: "Auto Posting", description: "Schedule and publish content automatically across all your connected social media platforms with one click." },
    { icon: "üìä", title: "Advanced Analytics", description: "Track performance with detailed analytics, engagement metrics, and growth insights for all your platforms." },
    { icon: "üéØ", title: "Smart Scheduling", description: "Optimize posting times for maximum engagement using AI-powered scheduling algorithms." },
    { icon: "‚ú®", title: "Premium Experience", description: "Enjoy a seamless, intuitive interface designed for both beginners and marketing professionals." }
  ];

  useEffect(() => {
    setShowLoginModal(true);
  }, []);

  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting
          ? fullText.substring(0, typedText.length - 1)
          : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 2000);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");

    if (newMode) {
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
    }
  };

  const openLoginModal = () => {
    setShowLoginModal(true);
    setError("");
    setSuccess("");
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);
    setError("");
    setSuccess("");
  };

  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark" || !saved) {
      setIsDarkMode(true);
      document.body.classList.add("dark");
    } else {
      setIsDarkMode(false);
      document.body.classList.remove("dark");
    }

    const rememberedUsername = localStorage.getItem("rememberedUsername");
    const rememberedRememberMe = localStorage.getItem("rememberMe") === "true";

    if (rememberedUsername && rememberedRememberMe) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }

    const token = localStorage.getItem("token");
    if (token) {
      const tokenExpiry = localStorage.getItem("tokenExpiry");
      if (tokenExpiry && parseInt(tokenExpiry) > Date.now()) {
        navigate("/connect", { replace: true });
      } else {
        localStorage.removeItem("token");
        localStorage.removeItem("tokenExpiry");
      }
    }
  }, [navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password, rememberMe }
      );

      const { token, user, rememberMe: rememberMeResponse, expiresIn } = response.data;

      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);

      const expiryTime = Date.now() + (expiresIn * 1000);
      localStorage.setItem("tokenExpiry", expiryTime.toString());

      if (rememberMeResponse) {
        localStorage.setItem("rememberedUsername", username);
        localStorage.setItem("rememberMe", "true");
      } else {
        localStorage.removeItem("rememberedUsername");
        localStorage.removeItem("rememberMe");
      }

      setSuccess("Login successful! Redirecting...");

      if (onLogin) {
        onLogin(user);
      }

      setTimeout(() => {
        setIsLoading(false);
        closeLoginModal();
        navigate("/connect", { replace: true });
      }, 1000);

    } catch (err) {
      const errorMessage = err.response?.data?.error || "Invalid username or password";
      setError(errorMessage);
      setIsLoading(false);
    }
  };

  const socialPlatforms = [
    { name: 'LinkedIn', icon: '/images/linkedin.png', color: '#0077B5' },
    { name: 'Instagram', icon: '/images/instagram.jpg', color: '#E1306C' },
    { name: 'Facebook', icon: '/images/facebook.png', color: '#1877F2' }
  ];

  return (
    <div className="futuristic-login-container">
      {/* Content */}
      <nav className="premium-navbar">
        <div className="nav-brand">
          <img src="/123.png" alt="Marketing Bot" className="nav-logo" />
          <span className="nav-brand-text">PostingExpert</span>
        </div>

        <div className="nav-links">
          <Link to="/login" className="nav-link active">Home</Link>
          <Link to="/about" className="nav-link">About</Link>
          <Link to="/privacy-policy" className="nav-link">Privacy Policy</Link>
          <Link to="/contact-us" className="nav-link">Contact Us</Link>
        </div>

        <div className="nav-controls">
          <button className="nav-login-btn" onClick={openLoginModal}>Login</button>
          <button className="theme-toggle-futuristic" onClick={toggleTheme}>
            {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>
      </nav>

      {/* Login Modal */}
      <div className={`login-modal-overlay ${showLoginModal ? 'active' : ''}`}>
        <div className="login-modal">
          <button className="login-modal-close" onClick={closeLoginModal}>‚úï</button>
          
          <div className="form-header-futuristic">
            <h2>Welcome Back</h2>
            <p>Access your AI marketing dashboard</p>
          </div>

          <form className="futuristic-form" onSubmit={handleSubmit}>
            {/* Input Fields */}
            {/* Submit Button */}
          </form>
        </div>
      </div>
    </div>
  );
};

export default Login;
