// InstagramConnect.js - COMPLETE VERSION WITH CALLBACK HANDLING

import React, { useState, useEffect } from 'react';

const InstagramConnect = ({ appUser, onConnected, connected, status }) => {
  const [isConnecting, setIsConnecting] = useState(false);

  // âœ… NEW: Listen for callback when component mounts
  useEffect(() => {
    // Check if this page is being used as the OAuth callback
    const urlParams = new URLSearchParams(window.location.search);
    const code = urlParams.get('code');
    const state = urlParams.get('state');
    const error = urlParams.get('error');
    const errorDescription = urlParams.get('error_description');

    // If we have OAuth parameters, this is a callback
    if (code || error) {
      handleOAuthCallback(code, state, error, errorDescription);
    }
  }, []);

  // âœ… NEW: Handle OAuth callback
  const handleOAuthCallback = async (code, state, error, errorDescription) => {
    // Handle error from Facebook
    if (error) {
      const errorMsg = errorDescription || error;
      console.error('âŒ Instagram OAuth error:', errorMsg);
      
      // Send message to parent window (if opened as popup)
      if (window.opener) {
        window.opener.postMessage({
          type: 'instagram_callback',
          success: false,
          error: errorMsg
        }, '*');
        setTimeout(() => window.close(), 2000);
      } else {
        alert(`Instagram connection failed: ${errorMsg}`);
      }
      return;
    }

    // Check if we have required parameters
    if (!code || !state) {
      console.error('âŒ Missing OAuth parameters');
      
      if (window.opener) {
        window.opener.postMessage({
          type: 'instagram_callback',
          success: false,
          error: 'Missing parameters'
        }, '*');
        setTimeout(() => window.close(), 2000);
      } else {
        alert('Instagram connection failed: Missing parameters');
      }
      return;
    }

    // Call backend to exchange code for token
    try {
      console.log('ðŸ“ž Calling backend to exchange code...');
      
      const response = await fetch('http://13.233.45.167:5000/social/instagram/callback', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          code: code,
          state: state
        })
      });

      const result = await response.json();

      if (result.success) {
        console.log('âœ… Instagram connected:', result);
        
        // Send success message to parent window
        if (window.opener) {
          window.opener.postMessage({
            type: 'instagram_callback',
            success: true,
            instagram_username: result.instagram_username,
            instagram_user_id: result.instagram_user_id,
            app_user: state
          }, '*');
          setTimeout(() => window.close(), 2000);
        } else {
          alert(`Instagram connected successfully! @${result.instagram_username}`);
          // Redirect to main page
          window.location.href = '/connect';
        }
      } else {
        console.error('âŒ Backend returned error:', result.error);
        
        if (window.opener) {
          window.opener.postMessage({
            type: 'instagram_callback',
            success: false,
            error: result.error || 'Connection failed'
          }, '*');
          setTimeout(() => window.close(), 2000);
        } else {
          alert(`Instagram connection failed: ${result.error}`);
        }
      }
    } catch (err) {
      console.error('âŒ Network error:', err);
      
      if (window.opener) {
        window.opener.postMessage({
          type: 'instagram_callback',
          success: false,
          error: 'Network error'
        }, '*');
        setTimeout(() => window.close(), 2000);
      } else {
        alert('Instagram connection failed: Network error');
      }
    }
  };

  const handleConnect = () => {
    setIsConnecting(true);

    // Instagram OAuth via Facebook Graph API
    const clientId = '1095157869184608'; // Main PostApp ID
    const redirectUri = encodeURIComponent('https://postingexpert.com/connect');
    const scope = encodeURIComponent('instagram_business_content_publish,instagram_business_basic,pages_show_list,pages_read_engagement');
    const state = encodeURIComponent(appUser || 'default_user');

    const url = `https://www.facebook.com/v21.0/dialog/oauth?client_id=${clientId}&redirect_uri=${redirectUri}&scope=${scope}&response_type=code&state=${state}`;

    console.log('ðŸ”— Opening OAuth URL:', url);

    // Open popup for Instagram OAuth
    const popup = window.open(
      url,
      'instagram_oauth',
      'width=600,height=700,scrollbars=yes,resizable=yes'
    );

    if (!popup) {
      alert('Popup blocked! Please allow popups for this site.');
      setIsConnecting(false);
      return;
    }

    // Listen for the OAuth callback message
    const handleMessage = (event) => {
      if (event.data.type === 'instagram_callback') {
        console.log('ðŸ“© Received callback message:', event.data);
        setIsConnecting(false);
        
        if (popup && !popup.closed) {
          popup.close();
        }

        if (event.data.success) {
          console.log('âœ… Instagram connected:', event.data);
          alert(`Instagram connected successfully! @${event.data.instagram_username}`);
          
          if (onConnected) {
            onConnected(); // Refresh status
          }
        } else {
          console.error('âŒ Instagram connection failed:', event.data.error);
          alert(`Instagram connection failed: ${event.data.error}`);
        }

        window.removeEventListener('message', handleMessage);
      }
    };

    window.addEventListener('message', handleMessage);

    // Handle popup close without callback
    const checkClosed = setInterval(() => {
      if (popup && popup.closed) {
        console.log('âš ï¸ Popup was closed');
        setIsConnecting(false);
        window.removeEventListener('message', handleMessage);
        clearInterval(checkClosed);
      }
    }, 1000);
  };

  const handleDisconnect = async () => {
    try {
      const response = await fetch('http://13.233.45.167:5000/social/instagram/disconnect', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({
          app_user: appUser
        })
      });

      const result = await response.json();

      if (result.success) {
        alert('Instagram disconnected successfully!');
        if (onConnected) {
          onConnected(); // Refresh status
        }
      } else {
        alert(`Failed to disconnect Instagram: ${result.error}`);
      }
    } catch (error) {
      console.error('Error disconnecting Instagram:', error);
      alert('Error disconnecting Instagram. Please try again.');
    }
  };

  const getButtonText = () => {
    if (isConnecting) return 'Connecting...';
    if (connected && status?.username) {
      return `Disconnect @${status.username}`;
    }
    return connected ? 'Disconnect Instagram' : 'Connect Instagram';
  };

  const getStatusText = () => {
    if (!connected) return null;

    if (!status) return 'Connected';

    return (
      <div style={{ fontSize: '12px', color: '#666', marginTop: '4px' }}>
        {status.username && `@${status.username}`}
        {status.page_name && ` via ${status.page_name}`}
        <br />
        {status.connected_at && `Connected: ${new Date(status.connected_at).toLocaleDateString()}`}
      </div>
    );
  };

  return (
    <div style={{ marginBottom: '15px' }}>
      <button
        className={`connect-button ${connected ? 'connected' : 'disconnected'}`}
        onClick={connected ? handleDisconnect : handleConnect}
        disabled={isConnecting}
        style={{
          backgroundColor: connected ? '#dc3545' : '#E1306C',
          color: 'white',
          border: 'none',
          padding: '10px 20px',
          borderRadius: '5px',
          cursor: isConnecting ? 'not-allowed' : 'pointer',
          opacity: isConnecting ? 0.6 : 1,
          fontSize: '14px',
          fontWeight: 'bold'
        }}
      >
        {getButtonText()}
      </button>
      {getStatusText()}
    </div>
  );
};

export default InstagramConnect;
