// src/components/ContactUs.js
import React, { useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./ContactUs.css";

const ContactUs = () => {
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    subject: '',
    message: ''
  });
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [submitStatus, setSubmitStatus] = useState('');
  const navigate = useNavigate();

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
    
    if (newMode) {
      document.body.classList.add("dark");
    } else {
      document.body.classList.remove("dark");
    }
  };

  React.useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark" || !saved) {
      setIsDarkMode(true);
      document.body.classList.add("dark");
    } else {
      setIsDarkMode(false);
      document.body.classList.remove("dark");
    }
  }, []);

  const handleInputChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    setSubmitStatus('');

    // Simulate form submission
    setTimeout(() => {
      setIsSubmitting(false);
      setSubmitStatus('success');
      setFormData({ name: '', email: '', subject: '', message: '' });
      
      // Reset status after 5 seconds
      setTimeout(() => setSubmitStatus(''), 5000);
    }, 2000);
  };

  const contactMethods = [
    {
      icon: 'üìß',
      title: 'Email Us',
      details: 'craftingbrain@gmail.com',
      description: 'Send us an email anytime'
    },
    {
      icon: 'üè¢',
      title: 'Visit Us',
      details: 'Inikola Technologies',
      description: 'Powered by innovation'
    },
    {
      icon: '‚è∞',
      title: 'Response Time',
      details: 'Within 24 hours',
      description: 'We reply quickly'
    },
    {
      icon: 'üåê',
      title: 'Office Hours',
      details: 'Mon - Fri, 9AM - 6PM',
      description: 'Local business hours'
    }
  ];

  const faqs = [
    {
      question: "How quickly do you respond to inquiries?",
      answer: "We typically respond within 24 hours during business days. For urgent matters, please mention 'URGENT' in your subject line."
    },
    {
      question: "Do you offer technical support?",
      answer: "Yes, we provide comprehensive technical support for all Marketing Bot users. Our support team is available to help with any issues."
    },
    {
      question: "Can I schedule a demo of Marketing Bot?",
      answer: "Absolutely! We'd love to show you how Marketing Bot can transform your social media strategy. Contact us to schedule a personalized demo."
    },
    {
      question: "Do you offer custom solutions?",
      answer: "Yes, we work with businesses to create custom AI-powered marketing solutions tailored to their specific needs."
    }
  ];

  return (
    <div className="contact-us-container">
      {/* Background Elements */}
      <div className="cyber-grid"></div>
      <div className="geometric-bg">
        <div className="shape"></div>
        <div className="shape"></div>
        <div className="shape"></div>
      </div>
      
      <div className="floating-particles">
        {[...Array(15)].map((_, i) => (
          <div key={i} className="particle"></div>
        ))}
      </div>

      {/* Theme Toggle */}
      <button className="theme-toggle-futuristic" onClick={toggleTheme}>
        {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
      </button>

      {/* Enhanced Navbar */}
      <nav className="premium-navbar">
        <div className="nav-brand">
          <img src="/123.png" alt="Marketing Bot" className="nav-logo" />
          <span className="nav-brand-text">Marketing Bot</span>
        </div>
        
        <div className="nav-links">
          <Link to="/" className="nav-link">Home</Link>
          <Link to="/privacy-policy" className="nav-link">Privacy Policy</Link>
          <Link to="/contact-us" className="nav-link active">Contact Us</Link>
          <button 
            className="nav-login-btn"
            onClick={() => navigate('/')}
          >
            Login
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <div className="contact-content">
        <div className="contact-header">
          <h1>Get In Touch</h1>
          <p className="contact-subtitle">We'd love to hear from you! Reach out and let's start a conversation.</p>
        </div>

        <div className="contact-grid">
          {/* Contact Information */}
          <div className="contact-info-section">
            <h2>üìû Contact Information</h2>
            <p>Choose the most convenient way to reach us</p>
            
            <div className="contact-methods">
              {contactMethods.map((method, index) => (
                <div key={index} className="contact-method-card">
                  <div className="method-icon">{method.icon}</div>
                  <div className="method-content">
                    <h3>{method.title}</h3>
                    <p className="method-details">{method.details}</p>
                    <p className="method-description">{method.description}</p>
                  </div>
                </div>
              ))}
            </div>

            <div className="additional-info">
              <h3>üí° Before Contacting Us</h3>
              <ul>
                <li>Have your account information ready if you're an existing user</li>
                <li>Describe your issue or question in detail</li>
                <li>Include any relevant screenshots or error messages</li>
                <li>Let us know your preferred contact method</li>
              </ul>
            </div>
          </div>

          {/* Contact Form */}
          <div className="contact-form-section">
            <div className="form-container">
              <h2>‚úâÔ∏è Send us a Message</h2>
              <p>Fill out the form below and we'll get back to you soon</p>
              
              <form className="contact-form" onSubmit={handleSubmit}>
                <div className="form-group">
                  <label htmlFor="name">Full Name *</label>
                  <input
                    type="text"
                    id="name"
                    name="name"
                    value={formData.name}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your full name"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="email">Email Address *</label>
                  <input
                    type="email"
                    id="email"
                    name="email"
                    value={formData.email}
                    onChange={handleInputChange}
                    required
                    placeholder="Enter your email address"
                  />
                </div>

                <div className="form-group">
                  <label htmlFor="subject">Subject *</label>
                  <select
                    id="subject"
                    name="subject"
                    value={formData.subject}
                    onChange={handleInputChange}
                    required
                  >
                    <option value="">Select a subject</option>
                    <option value="general">General Inquiry</option>
                    <option value="support">Technical Support</option>
                    <option value="demo">Request a Demo</option>
                    <option value="partnership">Partnership</option>
                    <option value="feedback">Feedback</option>
                    <option value="other">Other</option>
                  </select>
                </div>

                <div className="form-group">
                  <label htmlFor="message">Message *</label>
                  <textarea
                    id="message"
                    name="message"
                    value={formData.message}
                    onChange={handleInputChange}
                    required
                    rows="6"
                    placeholder="Tell us how we can help you..."
                  ></textarea>
                </div>

                {submitStatus === 'success' && (
                  <div className="success-message">
                    ‚úÖ Thank you! Your message has been sent successfully. We'll get back to you soon.
                  </div>
                )}

                <button 
                  type="submit" 
                  className="submit-btn"
                  disabled={isSubmitting}
                >
                  {isSubmitting ? (
                    <>
                      <span className="loading-spinner"></span>
                      Sending...
                    </>
                  ) : (
                    'Send Message'
                  )}
                </button>
              </form>
            </div>
          </div>
        </div>

        {/* FAQ Section */}
        <div className="faq-section">
          <h2>‚ùì Frequently Asked Questions</h2>
          <div className="faq-grid">
            {faqs.map((faq, index) => (
              <div key={index} className="faq-card">
                <h3>{faq.question}</h3>
                <p>{faq.answer}</p>
              </div>
            ))}
          </div>
        </div>

        {/* Additional Contact */}
        <div className="additional-contact">
          <h2>üöÄ Ready to Transform Your Social Media?</h2>
          <p>Join hundreds of businesses using Marketing Bot to automate their social media presence</p>
          <div className="cta-buttons">
            <button 
              className="cta-btn primary"
              onClick={() => navigate('/')}
            >
              Start Free Trial
            </button>
            <button 
              className="cta-btn secondary"
              onClick={() => window.open('mailto:craftingbrain@gmail.com')}
            >
              Schedule Demo
            </button>
          </div>
        </div>
      </div>

      {/* Footer */}
      <footer className="premium-footer">
        <div className="footer-content">
          <div className="footer-section">
            <h4>Marketing Bot</h4>
            <p>AI-Powered Social Media Automation</p>
            <p className="footer-tagline">Built by Inikola √ó CraftingBrain</p>
          </div>
          <div className="footer-section">
            <h4>Quick Links</h4>
            <Link to="/">Home</Link>
            <Link to="/privacy-policy">Privacy Policy</Link>
            <Link to="/contact-us">Contact Us</Link>
          </div>
          <div className="footer-section">
            <h4>Connect</h4>
            <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">LinkedIn</a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">Instagram</a>
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">Facebook</a>
          </div>
        </div>
        <div className="footer-bottom">
          <p>¬© 2025 Marketing Bot. All rights reserved. | Powered by Inikola Technologies & CraftingBrain</p>
        </div>
      </footer>
    </div>
  );
};

export default ContactUs;
