import React, { useState } from 'react';

const EnhancedLogin = () => {
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [formData, setFormData] = useState({ username: '', password: '', rememberMe: false });
  
  const features = [
    { icon: 'ðŸŽ¨', title: 'AI-Powered Content', desc: 'Generate professional images and captions instantly' },
    { icon: 'ðŸ“±', title: 'Multi-Platform Posting', desc: 'Auto-post to Instagram, LinkedIn & more' },
    { icon: 'âš¡', title: 'Smart Scheduling', desc: 'Optimize posting times for maximum reach' },
    { icon: 'ðŸ“Š', title: 'Analytics Dashboard', desc: 'Track performance & engagement metrics' }
  ];

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Form submitted:', formData);
  };

  return (
    <div className={`login-wrapper ${isDarkMode ? 'dark' : 'light'}`}>
      <style>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&display=swap');
        
        * { margin: 0; padding: 0; box-sizing: border-box; }
        
        .login-wrapper {
          min-height: 100vh;
          font-family: 'Inter', -apple-system, BlinkMacSystemFont, sans-serif;
          position: relative;
          overflow-x: hidden;
          transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
        }
        
        .login-wrapper.light {
          background: linear-gradient(135deg, #fafafa 0%, #ffffff 50%, #f5f5f5 100%);
          color: #171717;
        }
        
        .login-wrapper.dark {
          background: linear-gradient(135deg, #171717 0%, #000000 50%, #262626 100%);
          color: #ffffff;
        }
        
        /* Animated Background */
        .bg-shapes {
          position: absolute;
          inset: 0;
          overflow: hidden;
          pointer-events: none;
          z-index: 0;
        }
        
        .shape {
          position: absolute;
          opacity: 0.03;
          animation: float 20s ease-in-out infinite;
        }
        
        .login-wrapper.dark .shape { opacity: 0.05; }
        
        .shape-1 {
          top: 10%;
          left: 10%;
          width: 400px;
          height: 400px;
          background: radial-gradient(circle, #000 0%, transparent 70%);
          animation-delay: 0s;
        }
        
        .shape-2 {
          bottom: 10%;
          right: 10%;
          width: 500px;
          height: 500px;
          background: radial-gradient(circle, #000 0%, transparent 70%);
          animation-delay: 3s;
        }
        
        @keyframes float {
          0%, 100% { transform: translate(0, 0) rotate(0deg); }
          33% { transform: translate(30px, -30px) rotate(120deg); }
          66% { transform: translate(-20px, 20px) rotate(240deg); }
        }
        
        /* Theme Toggle */
        .theme-toggle {
          position: fixed;
          top: 2rem;
          left: 2rem;
          width: 56px;
          height: 56px;
          border-radius: 16px;
          border: 2px solid;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1.5rem;
          cursor: pointer;
          z-index: 100;
          backdrop-filter: blur(20px);
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        
        .login-wrapper.light .theme-toggle {
          background: rgba(255, 255, 255, 0.8);
          border-color: #e5e5e5;
          box-shadow: 0 10px 40px rgba(0,0,0,0.05);
        }
        
        .login-wrapper.dark .theme-toggle {
          background: rgba(38, 38, 38, 0.8);
          border-color: #404040;
          box-shadow: 0 10px 40px rgba(0,0,0,0.3);
        }
        
        .theme-toggle:hover {
          transform: translateY(-4px) rotate(15deg);
          box-shadow: 0 20px 50px rgba(0,0,0,0.15);
        }
        
        /* Main Container */
        .login-container {
          display: flex;
          min-height: 100vh;
          position: relative;
          z-index: 1;
          padding: 2rem;
          gap: 4rem;
          max-width: 1400px;
          margin: 0 auto;
          align-items: center;
        }
        
        /* Brand Section */
        .brand-section {
          flex: 1;
          padding: 2rem;
          animation: slideInLeft 0.8s cubic-bezier(0.4, 0, 0.2, 1);
        }
        
        @keyframes slideInLeft {
          from { opacity: 0; transform: translateX(-50px); }
          to { opacity: 1; transform: translateX(0); }
        }
        
        .brand-header {
          display: flex;
          align-items: center;
          gap: 1.5rem;
          margin-bottom: 3rem;
        }
        
        .brand-logo {
          width: 80px;
          height: 80px;
          border-radius: 20px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 2.5rem;
          background: linear-gradient(135deg, #000 0%, #404040 100%);
          box-shadow: 0 10px 30px rgba(0,0,0,0.2);
          transition: transform 0.3s ease;
        }
        
        .login-wrapper.dark .brand-logo {
          background: linear-gradient(135deg, #fff 0%, #d4d4d4 100%);
        }
        
        .brand-logo:hover {
          transform: scale(1.05) rotate(5deg);
        }
        
        .brand-info h1 {
          font-size: 2.5rem;
          font-weight: 800;
          letter-spacing: -0.03em;
          margin-bottom: 0.5rem;
        }
        
        .brand-info p {
          font-size: 1rem;
          opacity: 0.7;
          font-weight: 500;
        }
        
        .brand-title {
          font-size: 3.5rem;
          font-weight: 900;
          line-height: 1.1;
          margin-bottom: 1.5rem;
          letter-spacing: -0.04em;
        }
        
        .brand-highlight {
          display: block;
          background: linear-gradient(135deg, #000 0%, #404040 100%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
          background-clip: text;
          margin-top: 0.5rem;
        }
        
        .login-wrapper.dark .brand-highlight {
          background: linear-gradient(135deg, #fff 0%, #d4d4d4 100%);
          -webkit-background-clip: text;
          -webkit-text-fill-color: transparent;
        }
        
        .brand-description {
          font-size: 1.125rem;
          line-height: 1.8;
          opacity: 0.8;
          margin-bottom: 3rem;
          max-width: 600px;
        }
        
        /* Feature Cards */
        .features-grid {
          display: grid;
          grid-template-columns: repeat(2, 1fr);
          gap: 1.25rem;
          margin-bottom: 3rem;
        }
        
        .feature-card {
          padding: 1.75rem;
          border-radius: 20px;
          border: 2px solid;
          backdrop-filter: blur(10px);
          transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
          cursor: pointer;
        }
        
        .login-wrapper.light .feature-card {
          background: rgba(255,255,255,0.6);
          border-color: #e5e5e5;
        }
        
        .login-wrapper.dark .feature-card {
          background: rgba(38,38,38,0.6);
          border-color: #404040;
        }
        
        .feature-card:hover {
          transform: translateY(-8px) translateX(4px);
          box-shadow: 0 20px 60px rgba(0,0,0,0.15);
        }
        
        .login-wrapper.dark .feature-card:hover {
          border-color: #525252;
          box-shadow: 0 20px 60px rgba(0,0,0,0.5);
        }
        
        .feature-icon {
          width: 56px;
          height: 56px;
          border-radius: 14px;
          display: flex;
          align-items: center;
          justify-content: center;
          font-size: 1.75rem;
          margin-bottom: 1.25rem;
          background: linear-gradient(135deg, #000 0%, #262626 100%);
        }
        
        .login-wrapper.dark .feature-icon {
          background: linear-gradient(135deg, #fff 0%, #e5e5e5 100%);
        }
        
        .feature-card h3 {
          font-size: 1.125rem;
          font-weight: 700;
          margin-bottom: 0.5rem;
          letter-spacing: -0.01em;
        }
        
        .feature-card p {
          font-size: 0.9375rem;
          opacity: 0.7;
          line-height: 1.6;
        }
        
        /* Social Proof */
        .social-proof {
          margin-top: 3rem;
          padding: 2rem;
          border-radius: 20px;
          border: 2px solid;
          display: flex;
          align-items: center;
          gap: 1.5rem;
          backdrop-filter: blur(10px);
        }
        
        .login-wrapper.light .social-proof {
          background: rgba(250,250,250,0.8);
          border-color: #e5e5e5;
        }
        
        .login-wrapper.dark .social-proof {
          background: rgba(23,23,23,0.8);
          border-color: #404040;
        }
        
        .proof-icon {
          font-size: 3rem;
          animation: bounce 2s ease-in-out infinite;
        }
        
        @keyframes bounce {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }
        
        .proof-content h4 {
          font-size: 1.375rem;
          font-weight: 700;
          margin-bottom: 0.5rem;
        }
        
        .proof-content p {
          font-size: 0.9375rem;
          opacity: 0.7;
        }
        
        /* Login Form Section */
        .login-section {
          width: 500px;
          padding: 3rem;
          border-radius: 24px;
          border: 2px solid;
          backdrop-filter: blur(20px);
          box-shadow: 0 20px 60px rgba(0,0,0,0.1);
          animation: slideInRight 0.8s cubic-bezier(0.4, 0, 0.2, 1);
        }
        
        @keyframes slideInRight {
          from { opacity: 0; transform: translateX(50px); }
          to { opacity: 1; transform: translateX(0); }
        }
        
        .login-wrapper.light .login-section {
          background: rgba(255,255,255,0.95);
          border-color: #e5e5e5;
        }
        
        .login-wrapper.dark .login-section {
          background: rgba(23,23,23,0.95);
          border-color: #404040;
        }
        
        .login-header {
          text-align: center;
          margin-bottom: 2.5rem;
        }
        
        .login-header h2 {
          font-size: 2rem;
          font-weight: 800;
          margin-bottom: 0.5rem;
          letter-spacing: -0.02em;
        }
        
        .login-header p {
          font-size: 0.9375rem;
          opacity: 0.7;
        }
        
        /* Form Elements */
        .login-form {
          display: flex;
          flex-direction: column;
          gap: 1.5rem;
        }
        
        .form-group label {
          display: block;
          font-size: 0.9375rem;
          font-weight: 600;
          margin-bottom: 0.75rem;
          margin-left: 0.25rem;
        }
        
        .form-group input {
          width: 100%;
          padding: 1rem 1.25rem;
          border-radius: 14px;
          border: 2px solid;
          font-size: 1rem;
          font-family: inherit;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
        }
        
        .login-wrapper.light .form-group input {
          background: #fafafa;
          border-color: #e5e5e5;
          color: #171717;
        }
        
        .login-wrapper.dark .form-group input {
          background: #262626;
          border-color: #404040;
          color: #ffffff;
        }
        
        .form-group input:focus {
          outline: none;
          border-color: #000;
          transform: translateY(-2px);
          box-shadow: 0 10px 30px rgba(0,0,0,0.1);
        }
        
        .login-wrapper.dark .form-group input:focus {
          border-color: #fff;
          box-shadow: 0 10px 30px rgba(255,255,255,0.1);
        }
        
        .form-group input::placeholder {
          color: #737373;
        }
        
        /* Password Toggle */
        .password-wrapper {
          position: relative;
        }
        
        .password-toggle {
          position: absolute;
          right: 1rem;
          top: 50%;
          transform: translateY(-50%);
          background: none;
          border: none;
          cursor: pointer;
          font-size: 1.25rem;
          opacity: 0.6;
          transition: all 0.2s ease;
          padding: 0.5rem;
          border-radius: 8px;
        }
        
        .password-toggle:hover {
          opacity: 1;
          background: rgba(0,0,0,0.05);
        }
        
        .login-wrapper.dark .password-toggle:hover {
          background: rgba(255,255,255,0.1);
        }
        
        /* Remember Me */
        .remember-me {
          display: flex;
          align-items: center;
          gap: 0.75rem;
        }
        
        .remember-me input[type="checkbox"] {
          width: 22px;
          height: 22px;
          border-radius: 6px;
          cursor: pointer;
        }
        
        .remember-me label {
          font-size: 0.9375rem;
          font-weight: 500;
          cursor: pointer;
          user-select: none;
        }
        
        /* Submit Button */
        .submit-btn {
          width: 100%;
          padding: 1.125rem;
          border-radius: 14px;
          border: none;
          font-size: 1rem;
          font-weight: 700;
          cursor: pointer;
          transition: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
          letter-spacing: 0.5px;
          text-transform: uppercase;
          position: relative;
          overflow: hidden;
        }
        
        .login-wrapper.light .submit-btn {
          background: linear-gradient(135deg, #000 0%, #262626 100%);
          color: #fff;
          box-shadow: 0 10px 30px rgba(0,0,0,0.2);
        }
        
        .login-wrapper.dark .submit-btn {
          background: linear-gradient(135deg, #fff 0%, #e5e5e5 100%);
          color: #000;
          box-shadow: 0 10px 30px rgba(255,255,255,0.2);
        }
        
        .submit-btn::before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(255,255,255,0.3), transparent);
          transition: left 0.5s ease;
        }
        
        .submit-btn:hover::before {
          left: 100%;
        }
        
        .submit-btn:hover {
          transform: translateY(-4px);
          box-shadow: 0 20px 40px rgba(0,0,0,0.3);
        }
        
        /* Form Footer */
        .form-footer {
          margin-top: 2rem;
          text-align: center;
        }
        
        .form-footer p {
          font-size: 0.9375rem;
          opacity: 0.7;
          margin-bottom: 1.5rem;
        }
        
        .form-footer a {
          color: inherit;
          font-weight: 600;
          text-decoration: none;
          border-bottom: 2px solid;
          padding-bottom: 2px;
          transition: all 0.2s ease;
        }
        
        /* Stats */
        .stats-container {
          display: flex;
          justify-content: space-around;
          margin-top: 2rem;
          padding-top: 2rem;
          border-top: 2px solid;
        }
        
        .login-wrapper.light .stats-container {
          border-top-color: #e5e5e5;
        }
        
        .login-wrapper.dark .stats-container {
          border-top-color: #404040;
        }
        
        .stat-item {
          text-align: center;
        }
        
        .stat-value {
          font-size: 2rem;
          font-weight: 900;
          letter-spacing: -0.02em;
        }
        
        .stat-label {
          font-size: 0.8125rem;
          opacity: 0.7;
          text-transform: uppercase;
          letter-spacing: 0.5px;
          font-weight: 600;
        }
        
        /* Responsive */
        @media (max-width: 1024px) {
          .login-container {
            flex-direction: column;
            padding: 1rem;
          }
          
          .brand-section {
            text-align: center;
          }
          
          .login-section {
            width: 100%;
            max-width: 500px;
          }
          
          .features-grid {
            grid-template-columns: 1fr;
          }
        }
        
        @media (max-width: 640px) {
          .theme-toggle {
            width: 48px;
            height: 48px;
            font-size: 1.25rem;
            top: 1rem;
            left: 1rem;
          }
          
          .brand-title {
            font-size: 2.5rem;
          }
          
          .login-section {
            padding: 2rem 1.5rem;
          }
        }
      `}</style>

      <div className="bg-shapes">
        <div className="shape shape-1"></div>
        <div className="shape shape-2"></div>
      </div>

      <button className="theme-toggle" onClick={() => setIsDarkMode(!isDarkMode)}>
        {isDarkMode ? 'â˜€ï¸' : 'ðŸŒ™'}
      </button>

      <div className="login-container">
        <div className="brand-section">
          <div className="brand-header">
            <div className="brand-logo">ðŸ¤–</div>
            <div className="brand-info">
              <h1>Marketing Bot</h1>
              <p>by INIKOLA</p>
            </div>
          </div>

          <h2 className="brand-title">
            START YOUR
            <span className="brand-highlight"> MARKETING REVOLUTION</span>
            <br />Join the AI-Powered Future
          </h2>

          <p className="brand-description">
            Transform your business with the power of AI. Join 1000+ forward-thinking entrepreneurs 
            who are already dominating social media. One platform, unlimited possibilities â€“ create, 
            schedule, and scale your brand like never before.
          </p>

          <div className="features-grid">
            {features.map((feature, index) => (
              <div key={index} className="feature-card">
                <div className="feature-icon">{feature.icon}</div>
                <h3>{feature.title}</h3>
                <p>{feature.desc}</p>
              </div>
            ))}
          </div>

          <div className="social-proof">
            <div className="proof-icon">ðŸš€</div>
            <div className="proof-content">
              <h4>Join 1000+ Growing Businesses</h4>
              <p>Trusted by startups and enterprises worldwide</p>
            </div>
          </div>
        </div>

        <div className="login-section">
          <div className="login-header">
            <h2>Get started with your account</h2>
            <p>Sign in to access your marketing dashboard</p>
          </div>

          <div className="login-form">
            <div className="form-group">
              <label>Username</label>
              <input
                type="text"
                placeholder="Enter username"
                value={formData.username}
                onChange={(e) => setFormData({...formData, username: e.target.value})}
              />
            </div>

            <div className="form-group">
              <label>Password</label>
              <div className="password-wrapper">
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter password"
                  value={formData.password}
                  onChange={(e) => setFormData({...formData, password: e.target.value})}
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                >
                  {showPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
                </button>
              </div>
            </div>

            <div className="remember-me">
              <input
                type="checkbox"
                id="remember"
                checked={formData.rememberMe}
                onChange={(e) => setFormData({...formData, rememberMe: e.target.checked})}
              />
              <label htmlFor="remember">Remember me for 30 days</label>
            </div>

            <button className="submit-btn" onClick={handleSubmit}>
              Get Started
            </button>
          </div>

          <div className="form-footer">
            <p>New user? <a href="/register">Register here</a></p>
          </div>

          <div className="stats-container">
            <div className="stat-item">
              <div className="stat-value">10+</div>
              <div className="stat-label">Active Users</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">50+</div>
              <div className="stat-label">Posts Created</div>
            </div>
            <div className="stat-item">
              <div className="stat-value">98%</div>
              <div className="stat-label">Satisfaction</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default EnhancedLogin;
