          <button 
            onClick={openLoginModal}
            type="button"
            style={loginButtonStyles}
            onMouseEnter={(e) => {
              e.target.style.transform = 'translateY(-2px)';
              e.target.style.boxShadow = isDarkMode ? '0 8px 25px rgba(0, 0, 0, 0.4)' : '0 4px 12px rgba(37, 99, 235, 0.3)';
