// src/components/Login.js
import React, { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [typedText, setTypedText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);
  const [showLoginModal, setShowLoginModal] = useState(true); // auto open

  const navigate = useNavigate();

  const phrases = useMemo(() => [
    'Generate AI Content',
    'Post to Multiple Platforms',
    'Smart Scheduling',
    'Boost Engagement',
    'Automate Marketing'
  ], []);

  // typewriter
  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting
          ? fullText.substring(0, typedText.length - 1)
          : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 1400);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  // theme + remembered username + token check + modal scroll lock + ESC
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "dark" || !saved) {
      setIsDarkMode(true);
      document.body.classList.add("dark");
    } else {
      setIsDarkMode(false);
      document.body.classList.remove("dark");
    }

    const rememberedUsername = localStorage.getItem("rememberedUsername");
    const rememberedRememberMe = localStorage.getItem("rememberMe") === "true";
    if (rememberedUsername && rememberedRememberMe) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }

    const token = localStorage.getItem("token");
    if (token) {
      const tokenExpiry = localStorage.getItem("tokenExpiry");
      if (tokenExpiry && parseInt(tokenExpiry, 10) > Date.now()) {
        navigate("/connect", { replace: true });
      } else {
        localStorage.removeItem("token");
        localStorage.removeItem("tokenExpiry");
      }
    }

    // lock scroll when modal is open
    const lock = () => document.body.style.overflow = 'hidden';
    const unlock = () => document.body.style.overflow = '';
    if (showLoginModal) lock(); else unlock();

    // ESC to close (if needed)
    const onKey = (e) => { if (e.key === 'Escape') setShowLoginModal(false); };
    window.addEventListener('keydown', onKey);
    return () => {
      window.removeEventListener('keydown', onKey);
      unlock();
    };
  }, [navigate, showLoginModal]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
    if (newMode) document.body.classList.add("dark");
    else document.body.classList.remove("dark");
  };

  const openLoginModal = () => {
    setShowLoginModal(true);
    setError("");
    setSuccess("");
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);
    setError("");
    setSuccess("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    try {
      const response = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password, rememberMe }
      );

      const { token, user, rememberMe: rememberMeResponse, expiresIn } = response.data;

      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);

      const expiryTime = Date.now() + (expiresIn * 1000);
      localStorage.setItem("tokenExpiry", expiryTime.toString());

      if (rememberMeResponse) {
        localStorage.setItem("rememberedUsername", username);
        localStorage.setItem("rememberMe", "true");
      } else {
        localStorage.removeItem("rememberedUsername");
        localStorage.removeItem("rememberMe");
      }

      setSuccess("Login successful! Redirecting...");

      if (onLogin) onLogin(user);

      setTimeout(() => {
        setIsLoading(false);
        closeLoginModal();
        navigate("/connect", { replace: true });
      }, 900);

    } catch (err) {
      const errorMessage = err.response?.data?.error || "Invalid username or password";
      setError(errorMessage);
      setIsLoading(false);
    }
  };

  const socialPlatforms = [
    { name: 'LinkedIn', icon: '/images/linkedin.png', color: '#0077B5' },
    { name: 'Instagram', icon: '/images/instagram.jpg', color: '#E1306C' },
    { name: 'Facebook', icon: '/images/facebook.png', color: '#1877F2' }
  ];

  const trustedCompanies = [
    { name: 'Inikola', logo: 'üè¢', description: 'Tech Innovation' },
    { name: 'CraftingBrain', logo: 'üß†', description: 'EdTech Company' }
  ];

  const navbarStyles = {
    position: 'fixed', top: 0, left: 0, right: 0,
    background: isDarkMode ? 'rgba(15,23,42,.65)' : 'rgba(255,255,255,.9)',
    backdropFilter: 'blur(16px)', WebkitBackdropFilter: 'blur(16px)',
    borderBottom: isDarkMode ? '1px solid rgba(255,255,255,.08)' : '1px solid #E2E8F0',
    padding: '1rem 2rem',
    display: 'flex', alignItems: 'center', justifyContent: 'space-between',
    zIndex: 9999
  };

  const leftSectionStyles = { display:'flex', alignItems:'center', gap:'1.2rem' };
  const logoStyles = {
    width:46, height:46, borderRadius:12, objectFit:'cover',
    background:'linear-gradient(135deg,#2563EB,#60A5FA)', padding:2
  };
  const brandTextStyles = {
    fontSize:'1.6rem', fontWeight: 900,
    background:'linear-gradient(135deg,#2563EB,#60A5FA)',
    WebkitBackgroundClip:'text', WebkitTextFillColor:'transparent'
  };
  const themeButtonStyles = {
    width:44, height:44, borderRadius:12,
    background: isDarkMode ? 'rgba(30,41,59,.6)' : 'rgba(248,250,252,.8)',
    border: isDarkMode ? '1px solid rgba(255,255,255,.08)' : '1px solid #E2E8F0',
    color: isDarkMode ? '#E2E8F0' : '#1E293B',
    fontSize:'1.2rem', display:'grid', placeItems:'center', cursor:'pointer'
  };

  const navLinksStyles = { display:'flex', gap:'2rem', alignItems:'center' };
  const navLinkStyles = { color: isDarkMode ? '#9FB0D0' : '#475569', textDecoration:'none', fontWeight:600 };
  const activeLinkStyles = { ...navLinkStyles, color:'#2563EB' };

  const modalOverlayClasses = `modal-overlay ${showLoginModal ? '' : 'hidden'}`;

  return (
    <div className={`login-container ${isDarkMode ? 'dark' : ''}`}>
      {/* Background layers */}
      <div className="cyber-grid"></div>
      <div className="geometric-bg">
        <div className="shape"></div><div className="shape"></div><div className="shape"></div>
        <div className="shape"></div><div className="shape"></div>
      </div>
      <div className="floating-particles">
        {Array.from({length:20}).map((_,i)=>(<div key={i} className="particle"></div>))}
      </div>

      {/* NAVBAR */}
      <nav style={navbarStyles}>
        <div style={leftSectionStyles}>
          <img src="/123.png" alt="Posting Expert" style={logoStyles}
               onError={(e)=>{ e.target.style.display='none'; }}/>
          <span style={brandTextStyles}>Posting Expert</span>
          <button
            onClick={toggleTheme}
            type="button"
            style={themeButtonStyles}
            title="Toggle theme"
          >
            {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>

        <div style={navLinksStyles}>
          <Link to="/login" style={activeLinkStyles}>Home</Link>
          <Link to="/about" style={navLinkStyles}>About</Link>
          <Link to="/privacy-policy" style={navLinkStyles}>Privacy Policy</Link>
          <Link to="/contact-us" style={navLinkStyles}>Contact Us</Link>
        </div>

        <div>
          <button
            onClick={openLoginModal}
            type="button"
            className="btn"
            style={{width:'auto', padding:'.75rem 1.2rem'}}
          >
            Login
          </button>
        </div>
      </nav>

      {/* LOGIN MODAL */}
      <div className={modalOverlayClasses} aria-hidden={!showLoginModal}>
        <div className="modal-content" role="dialog" aria-modal="true" aria-label="Login">
          <button className="modal-close" onClick={closeLoginModal} type="button" title="Close (Esc)">‚úï</button>

          <div className="form-header">
            <h2>Welcome Back</h2>
            <p>Access your AI marketing dashboard</p>
          </div>

          <form className="login-form" onSubmit={handleSubmit}>
            <div className="form-group">
              <label>USERNAME</label>
              <input
                type="text"
                placeholder="Enter username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                autoComplete="off"
                required
              />
            </div>

            <div className="form-group">
              <label>PASSWORD</label>
              <div className="password-wrapper">
                <input
                  type={showPassword ? "text" : "password"}
                  placeholder="Enter password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  autoComplete="off"
                  required
                />
                <button
                  type="button"
                  className="password-toggle"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label="Toggle password visibility"
                >
                  {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                </button>
              </div>
            </div>

            <div className="checkbox-group">
              <input
                id="remember"
                type="checkbox"
                checked={rememberMe}
                onChange={(e) => setRememberMe(e.target.checked)}
              />
              <label htmlFor="remember">Remember me for 30 days</label>
            </div>

            {error && (
              <div className="alert alert-error">
                <span className="alert-icon">‚ö†Ô∏è</span>
                <span>{error}</span>
              </div>
            )}

            {success && (
              <div className="alert alert-success">
                <span className="alert-icon">‚úì</span>
                <span>{success}</span>
              </div>
            )}

            <button type="submit" className="btn" disabled={isLoading}>
              {isLoading ? (
                <>
                  <span className="loading-spinner"></span>
                  <span>Processing...</span>
                </>
              ) : (
                <>
                  <span>LAUNCH DASHBOARD</span>
                  <span className="btn-arrow">‚Üí</span>
                </>
              )}
            </button>
          </form>

          <div className="form-footer">
            <p>
              New to Posting Expert?{' '}
              <Link to="/register">Create Account</Link>
            </p>
          </div>
        </div>
      </div>

      {/* HERO (unchanged content, benefits) */}
      <div id="home" className="content-wrapper">
        <div className="hero">
          <div className="brand-header">
            <div className="brand-presents">Powered by Inikola Technologies</div>
            <div className="brand-main">
              <div className="logo-container">
                <img src="/123.png" alt="Posting Expert" className="brand-logo" />
              </div>
              <div className="brand-info">
                <h1>Posting Expert</h1>
                <p className="brand-tagline">AI-Powered Social Media Automation</p>
              </div>
            </div>
          </div>

          <div className="hero-typing-container">
            <h2 className="hero-title">
              <span className="gradient-text">{typedText}</span>
              <span className="cursor-blink">|</span>
            </h2>
            <p className="hero-subtitle">
              Transform your social media strategy with cutting-edge AI technology.
              Generate stunning content, schedule posts, and grow your presence across multiple platforms.
            </p>
          </div>

          <div className="hero-cta-section" style={{margin:'28px 0'}}>
            <button className="btn" onClick={openLoginModal} type="button" style={{width:'auto', padding:'1rem 1.4rem'}}>
              <span>Get Started Now</span>
              <span className="btn-arrow">‚Üí</span>
            </button>
          </div>
        </div>
      </div>

      <footer className="footer">
        <div className="footer-bottom">
          <p>¬© 2025 Posting Expert. All rights reserved. | Powered by Inikola Technologies & CraftingBrain</p>
        </div>
      </footer>
    </div>
  );
};

export default Login;
