// src/components/Register.js - Sage Green Theme Only
import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import './Register.css';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    username: '',
    password: '',
    confirmPassword: ''
  });
  const [success, setSuccess] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [validationErrors, setValidationErrors] = useState({});
  const [passwordStrength, setPasswordStrength] = useState(0);
  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [showRegisterModal, setShowRegisterModal] = useState(true);
  const [typedText, setTypedText] = useState('');
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(150);
  
  const navigate = useNavigate();

  const phrases = useMemo(() => [
    'Join the Revolution',
    'Start Creating Content',
    'Automate Your Marketing',
    'Grow Your Business',
    'AI-Powered Success'
  ], []);

  useEffect(() => {
    const handleType = () => {
      const current = loopNum % phrases.length;
      const fullText = phrases[current];

      setTypedText(
        isDeleting
          ? fullText.substring(0, typedText.length - 1)
          : fullText.substring(0, typedText.length + 1)
      );

      setTypingSpeed(isDeleting ? 30 : 100);

      if (!isDeleting && typedText === fullText) {
        setTimeout(() => setIsDeleting(true), 2000);
      } else if (isDeleting && typedText === '') {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };

    const timer = setTimeout(handleType, typingSpeed);
    return () => clearTimeout(timer);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  useEffect(() => {
    if (formData.password) {
      calculatePasswordStrength(formData.password);
    } else {
      setPasswordStrength(0);
    }
  }, [formData.password]);

  const calculatePasswordStrength = (password) => {
    let strength = 0;
    if (password.length >= 6) strength++;
    if (password.length >= 10) strength++;
    if (/[a-z]/.test(password) && /[A-Z]/.test(password)) strength++;
    if (/\d/.test(password)) strength++;
    if (/[^a-zA-Z0-9]/.test(password)) strength++;
    setPasswordStrength(strength);
  };

  const getPasswordStrengthLabel = () => {
    if (passwordStrength === 0) return '';
    if (passwordStrength <= 2) return 'Weak';
    if (passwordStrength <= 3) return 'Fair';
    if (passwordStrength <= 4) return 'Good';
    return 'Strong';
  };

  const getPasswordStrengthColor = () => {
    if (passwordStrength <= 2) return '#dc2626';
    if (passwordStrength <= 3) return '#f59e0b';
    if (passwordStrength <= 4) return '#10b981';
    return '#16a34a';
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    if (validationErrors[name]) {
      setValidationErrors(prev => ({ ...prev, [name]: '' }));
    }
    
    if (error) setError('');
  };

  const validateForm = () => {
    const errors = {};

    if (!formData.name.trim()) {
      errors.name = 'Name is required';
    } else if (formData.name.trim().length < 2) {
      errors.name = 'Name must be at least 2 characters';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!formData.email.trim()) {
      errors.email = 'Email is required';
    } else if (!emailRegex.test(formData.email)) {
      errors.email = 'Please enter a valid email address';
    }

    if (!formData.username.trim()) {
      errors.username = 'Username is required';
    } else if (formData.username.trim().length < 3) {
      errors.username = 'Username must be at least 3 characters';
    } else if (!/^[a-zA-Z0-9_]+$/.test(formData.username)) {
      errors.username = 'Username can only contain letters, numbers, and underscores';
    }

    if (!formData.password) {
      errors.password = 'Password is required';
    } else if (formData.password.length < 6) {
      errors.password = 'Password must be at least 6 characters';
    }

    if (!formData.confirmPassword) {
      errors.confirmPassword = 'Please confirm your password';
    } else if (formData.password !== formData.confirmPassword) {
      errors.confirmPassword = 'Passwords do not match';
    }

    return errors;
  };

  const closeRegisterModal = () => {
    setShowRegisterModal(false);
    setError('');
    setSuccess('');
    navigate('/login');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    
    const errors = validateForm();
    if (Object.keys(errors).length > 0) {
      setValidationErrors(errors);
      return;
    }

    setValidationErrors({});
    setIsLoading(true);

    try {
      const response = await fetch(
        'https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/register',
        {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            name: formData.name.trim(),
            email: formData.email.toLowerCase().trim(),
            username: formData.username.trim(),
            password: formData.password,
            confirmPassword: formData.confirmPassword 
          }),
        }
      );

      const data = await response.json();

      if (response.ok) {
        setSuccess('Registration successful! Taking you to the survey...');
        
        localStorage.setItem('registeredUserId', formData.username.trim());
        localStorage.setItem('username', formData.username.trim());
        
        if (data.token) {
          localStorage.setItem('token', data.token);
        }
        
        setFormData({
          name: '',
          email: '',
          username: '',
          password: '',
          confirmPassword: ''
        });
        
        setTimeout(() => {
          navigate('/survey');
        }, 1500);

      } else {
        if (data.error) {
          const errorMessage = data.error.toLowerCase();
          
          if (errorMessage.includes('username') && (errorMessage.includes('already') || errorMessage.includes('exists'))) {
            setValidationErrors({ 
              username: 'Username already taken. Please choose a different username.' 
            });
          } 
          else if (errorMessage.includes('email') && (errorMessage.includes('already') || errorMessage.includes('registered'))) {
            setValidationErrors({ 
              email: 'This email is already registered. Please use a different email or try logging in.' 
            });
          } 
          else if (errorMessage.includes('user') && errorMessage.includes('already')) {
            setError('An account with these details already exists. Please try logging in instead.');
          }
          else {
            setError(data.error);
          }
        } 
        else {
          if (response.status === 409) {
            setError('User already exists. Please try logging in or use different credentials.');
          } else if (response.status === 400) {
            setError('Invalid registration data. Please check all fields and try again.');
          } else {
            setError('Registration failed. Please try again.');
          }
        }
      }
    } catch (err) {
      console.error('Registration error:', err);
      setError('Network error. Please check your connection and try again.');
    } finally {
      setIsLoading(false);
    }
  };

  const socialPlatforms = [
    { name: 'LinkedIn', icon: '/images/linkedin.png' },
    { name: 'Instagram', icon: '/images/instagram.jpg' },
    { name: 'Facebook', icon: '/images/facebook.png' }
  ];

  const horizontalFeatures = [
    {
      icon: "ðŸ”—",
      title: "Multi-Platform Integration",
      description: "Connect to LinkedIn, Instagram, Facebook, Twitter, and more."
    },
    {
      icon: "ðŸ¤–",
      title: "AI Content Creation",
      description: "Generate engaging posts and stunning images using AI."
    },
    {
      icon: "ðŸ“¤",
      title: "Auto Posting",
      description: "Schedule and publish content automatically."
    }
  ];

  return (
    <div className="register-container">
      {/* NAVBAR */}
      <nav className="navbar">
        <div className="nav-left">
          <img 
            src="/123.png" 
            alt="Posting Expert" 
            className="nav-logo"
            onError={(e) => {
              e.target.style.display = 'none';
            }}
          />
          <span className="nav-brand">Posting Expert</span>
        </div>
        
        <div className="nav-links">
          <Link to="/login" className="nav-link">Home</Link>
          <Link to="/about" className="nav-link">About</Link>
          <Link to="/privacy-policy" className="nav-link">Privacy Policy</Link>
          <Link to="/contact-us" className="nav-link">Contact Us</Link>
        </div>

        <div className="nav-right">
          <button onClick={() => navigate('/login')} className="nav-login-btn">
            Back to Login
          </button>
        </div>
      </nav>

      {/* REGISTER MODAL */}
      {showRegisterModal && (
        <div className="modal-overlay" onClick={closeRegisterModal}>
          <div className="register-modal" onClick={(e) => e.stopPropagation()}>
            <button className="modal-close" onClick={closeRegisterModal}>âœ•</button>
            
            <div className="form-header">
              <h2>Create Your Account</h2>
              <p>Join thousands of successful marketers</p>
            </div>

            <form className="register-form" onSubmit={handleSubmit}>
              <div className="input-group">
                <label>FULL NAME</label>
                <input
                  type="text"
                  name="name"
                  placeholder="Enter your full name"
                  value={formData.name}
                  onChange={handleInputChange}
                  className={validationErrors.name ? 'error' : ''}
                  autoComplete="off"
                  required
                />
                {validationErrors.name && (
                  <span className="field-error">{validationErrors.name}</span>
                )}
              </div>

              <div className="input-group">
                <label>EMAIL ADDRESS</label>
                <input
                  type="email"
                  name="email"
                  placeholder="your.email@example.com"
                  value={formData.email}
                  onChange={handleInputChange}
                  className={validationErrors.email ? 'error' : ''}
                  autoComplete="off"
                  required
                />
                {validationErrors.email && (
                  <span className="field-error">{validationErrors.email}</span>
                )}
              </div>

              <div className="input-group">
                <label>USERNAME</label>
                <input
                  type="text"
                  name="username"
                  placeholder="Choose a unique username"
                  value={formData.username}
                  onChange={handleInputChange}
                  className={validationErrors.username ? 'error' : ''}
                  autoComplete="off"
                  required
                />
                {validationErrors.username && (
                  <span className="field-error">{validationErrors.username}</span>
                )}
                <small className="field-hint">Letters, numbers, and underscores only. Min 3 characters.</small>
              </div>

              <div className="input-group">
                <label>PASSWORD</label>
                <div className="password-wrapper">
                  <input
                    type={showPassword ? "text" : "password"}
                    name="password"
                    placeholder="Create a strong password"
                    value={formData.password}
                    onChange={handleInputChange}
                    className={validationErrors.password ? 'error' : ''}
                    autoComplete="off"
                    required
                  />
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? 'ðŸ‘ï¸' : 'ðŸ‘ï¸â€ðŸ—¨ï¸'}
                  </button>
                </div>
                {formData.password && (
                  <div className="password-strength-indicator">
                    <div className="strength-bar">
                      <div 
                        className="strength-fill"
                        style={{
                          width: `${(passwordStrength / 5) * 100}%`,
                          background: getPasswordStrengthColor()
                        }}
                      ></div>
                    </div>
