{/* <DOCUMENT filename="Login.js"> */}
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Login.css';

const Home = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [showLoginModal, setShowLoginModal] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'light') {
      setIsDarkMode(false);
      document.body.classList.remove('dark');
      document.body.classList.add('light');
    } else {
      setIsDarkMode(true);
      document.body.classList.add('dark');
      document.body.classList.remove('light');
    }
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    
    if (newMode) {
      document.body.classList.add('dark');
      document.body.classList.remove('light');
    } else {
      document.body.classList.remove('dark');
      document.body.classList.add('light');
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('http://13.233.45.167:5000/user/login', {
        username,
        password,
      });

      const { token } = response.data;
      const expiryTime = Date.now() + 60 * 60 * 1000;
      
      localStorage.setItem('token', token);
      localStorage.setItem('tokenExpiry', expiryTime);
      localStorage.setItem('username', username);
      
      navigate('/connect');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className={`login-container ${isDarkMode ? 'dark' : 'light'}`}>
      {/* Animated Background with Moving Gradient */}
      <div className="animated-bg">
        <div className="gradient-orb orb-1"></div>
        <div className="gradient-orb orb-2"></div>
        <div className="gradient-orb orb-3"></div>
        <div className="gradient-orb orb-4"></div>
        <div className="gradient-orb orb-5"></div>
      </div>

      {/* Professional Navigation Bar */}
      <nav className="login-navbar">
        <div className="navbar-container">
          <Link to="/" className="navbar-brand">
            <img src="/logo.png" alt="PostingExpert" className="navbar-logo" />
            <span className="brand-name">PostingExpert</span>
          </Link>
          
          <div className="navbar-menu">
            <Link to="/" className="nav-item active">Home</Link>
            <Link to="/about" className="nav-item">About Us</Link>
            <Link to="/contact" className="nav-item">Contact</Link>
            <Link to="/privacy" className="nav-item">Privacy</Link>
            <Link to="/register" className="nav-item nav-cta">Sign Up</Link>
          </div>

          <button className="theme-toggle-btn" onClick={toggleTheme}>
            {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
          </button>
        </div>
      </nav>

      {/* Main Content */}
      <div className="login-content">
        {/* Left Side - Branding */}
        <div className="login-branding">
          <div className="brand-section">
            <div className="brand-logo-circle">
              <img src="/logo.png" alt="PostingExpert" className="brand-logo-img" />
            </div>
            <h1 className="brand-title">
              Posting<span className="gradient-text">Expert</span>
            </h1>
            <p className="brand-subtitle">
              AI-Powered Social Media Automation with Auto Content & Image Generation, Posting, Analytics, and CRM Integration
            </p>
          </div>

          <div className="features-showcase">
            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <span className="feature-icon">ü§ñ</span>
              </div>
              <div className="feature-content">
                <h3>Generate AI Content</h3>
                <p>Create engaging text and images with advanced AI technology</p>
              </div>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <span className="feature-icon">üåê</span>
              </div>
              <div className="feature-content">
                <h3>Post to Multiple Platforms</h3>
                <p>Share content across all major social networks seamlessly</p>
              </div>
            </div>
            
            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <span className="feature-icon">‚è∞</span>
              </div>
              <div className="feature-content">
                <h3>Smart Scheduling</h3>
                <p>Optimal timing for maximum reach and engagement</p>
              </div>
            </div>

            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <span className="feature-icon">üìà</span>
              </div>
              <div className="feature-content">
                <h3>Boost Engagement</h3>
                <p>AI-driven tools to increase interactions and growth</p>
              </div>
            </div>

            <div className="feature-card">
              <div className="feature-icon-wrapper">
                <span className="feature-icon">üöÄ</span>
              </div>
              <div className="feature-content">
                <h3>Automate Marketing</h3>
                <p>End-to-end automation with CRM integration and analytics</p>
              </div>
            </div>
          </div>

          <div className="stats-grid">
            <div className="stat-card">
              <span className="stat-value">10K+</span>
              <span className="stat-label">Active Users</span>
            </div>
            <div className="stat-card">
              <span className="stat-value">50K+</span>
              <span className="stat-label">Posts Created</span>
            </div>
            <div className="stat-card">
              <span className="stat-value">4.9‚òÖ</span>
              <span className="stat-label">User Rating</span>
            </div>
          </div>
        </div>
      </div>

      {/* Login Modal Popup */}
      {showLoginModal && (
        <div className="login-modal">
          <div className="modal-overlay" onClick={() => setShowLoginModal(false)}></div>
          <div className="modal-box">
            <div className="dna-animation">
              <div className="dna-strand strand1"></div>
              <div className="dna-strand strand2"></div>
            </div>
            <div className="form-card">
              <div className="form-header">
                <h2 className="form-title">Welcome Back!</h2>
                <p className="form-description">Sign in to continue to PostingExpert</p>
              </div>

              <form className="login-form" onSubmit={handleSubmit}>
                <div className="input-group">
                  <label htmlFor="username" className="input-label">Username</label>
                  <div className="input-container">
                    <span className="input-icon">üë§</span>
                    <input
                      type="text"
                      id="username"
                      className="form-input"
                      value={username}
                      onChange={(e) => setUsername(e.target.value)}
                      placeholder="Enter your username"
                      required
                      disabled={loading}
                    />
                  </div>
                </div>

                <div className="input-group">
                  <label htmlFor="password" className="input-label">Password</label>
                  <div className="input-container">
                    <span className="input-icon">üîí</span>
                    <input
                      type="password"
                      id="password"
                      className="form-input"
                      value={password}
                      onChange={(e) => setPassword(e.target.value)}
                      placeholder="Enter your password"
                      required
                      disabled={loading}
                    />
                  </div>
                </div>

                <div className="form-options">
                  <label className="checkbox-label">
                    <input type="checkbox" className="checkbox-input" />
                    <span>Remember me</span>
                  </label>
                  <Link to="#" className="forgot-link">
                    Forgot password?
                  </Link>
                </div>

                {error && (
                  <div className="error-alert">
                    <span className="alert-icon">‚ö†Ô∏è</span>
                    <span className="alert-text">{error}</span>
                  </div>
                )}

                <button type="submit" className="submit-btn" disabled={loading}>
                  {loading ? (
                    <>
                      <span className="btn-spinner"></span>
                      <span>Signing in...</span>
                    </>
                  ) : (
                    <>
                      <span>Sign In</span>
                      <span className="btn-arrow">‚Üí</span>
                    </>
                  )}
                </button>

                <div className="form-divider">
                  <span className="divider-text">OR</span>
                </div>

                <div className="social-buttons">
                  <button type="button" className="social-btn google-btn">
                    <span className="social-icon">üîç</span>
                    <span>Continue with Google</span>
                  </button>
                  <button type="button" className="social-btn linkedin-btn">
                    <span className="social-icon">üíº</span>
                    <span>Continue with LinkedIn</span>
                  </button>
                </div>

                <div className="signup-section">
                  <span className="signup-text">Don't have an account?</span>
                  <Link to="/register" className="signup-link">
                    Sign up now
                  </Link>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Footer */}
      <footer className="login-footer">
        <div className="footer-links">
          <Link to="/privacy" className="footer-link">Privacy Policy</Link>
          <span className="footer-divider">‚Ä¢</span>
          <Link to="/contact" className="footer-link">Contact Support</Link>
          <span className="footer-divider">‚Ä¢</span>
          <Link to="/about" className="footer-link">About Us</Link>
        </div>
        <p className="footer-copyright">
          ¬© 2025 PostingExpert by CraftingBrain. All rights reserved.
        </p>
      </footer>
    </div>
  );
};

export default Home;
