// src/components/Login.js
import React, { useState, useEffect, useMemo } from "react";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";
import "./Login.css";

const Login = ({ onLogin }) => {
  // state
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [showPassword, setShowPassword] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(true); // auto open

  // typewriter
  const phrases = useMemo(
    () => [
      "Generate AI Content",
      "Post to Multiple Platforms",
      "Smart Scheduling",
      "Boost Engagement",
      "Automate Marketing",
    ],
    []
  );
  const [typedText, setTypedText] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopNum, setLoopNum] = useState(0);
  const [typingSpeed, setTypingSpeed] = useState(100);

  useEffect(() => {
    const tick = () => {
      const i = loopNum % phrases.length;
      const full = phrases[i];

      setTypedText(isDeleting ? full.slice(0, typedText.length - 1) : full.slice(0, typedText.length + 1));
      setTypingSpeed(isDeleting ? 28 : 100);

      if (!isDeleting && typedText === full) setTimeout(() => setIsDeleting(true), 1400);
      else if (isDeleting && typedText === "") {
        setIsDeleting(false);
        setLoopNum(loopNum + 1);
      }
    };
    const t = setTimeout(tick, typingSpeed);
    return () => clearTimeout(t);
  }, [typedText, isDeleting, loopNum, typingSpeed, phrases]);

  const navigate = useNavigate();

  // initial settings + modal scroll lock + ESC
  useEffect(() => {
    const saved = localStorage.getItem("theme");
    if (saved === "light") setIsDarkMode(false);

    const rememberedUsername = localStorage.getItem("rememberedUsername");
    const rememberedRememberMe = localStorage.getItem("rememberMe") === "true";
    if (rememberedUsername && rememberedRememberMe) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }

    const token = localStorage.getItem("token");
    const tokenExpiry = localStorage.getItem("tokenExpiry");
    if (token && tokenExpiry && parseInt(tokenExpiry, 10) > Date.now()) {
      navigate("/connect", { replace: true });
    }

    document.body.style.overflow = showLoginModal ? "hidden" : "";
    const onKey = (e) => e.key === "Escape" && setShowLoginModal(false);
    window.addEventListener("keydown", onKey);
    return () => {
      window.removeEventListener("keydown", onKey);
      document.body.style.overflow = "";
    };
  }, [navigate, showLoginModal]);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem("theme", newMode ? "dark" : "light");
  };

  const openLoginModal = () => {
    setShowLoginModal(true);
    setError("");
    setSuccess("");
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);
    setError("");
    setSuccess("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);

    try {
      const res = await axios.post(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        { username, password, rememberMe }
      );

      const { token, user, rememberMe: rememberResp, expiresIn } = res.data;
      localStorage.setItem("token", token);
      localStorage.setItem("username", user.username);
      localStorage.setItem("tokenExpiry", (Date.now() + expiresIn * 1000).toString());
      if (rememberResp) {
        localStorage.setItem("rememberedUsername", username);
        localStorage.setItem("rememberMe", "true");
      } else {
        localStorage.removeItem("rememberedUsername");
        localStorage.removeItem("rememberMe");
      }

      setSuccess("Login successful! Redirecting...");
      if (onLogin) onLogin(user);
      setTimeout(() => {
        setIsLoading(false);
        closeLoginModal();
        navigate("/connect", { replace: true });
      }, 900);
    } catch (err) {
      const msg = err.response?.data?.error || "Invalid username or password";
      setError(msg);
      setIsLoading(false);
    }
  };

  // inline styles for navbar (kept simple, rest in CSS)
  const navbarStyles = {
    position: "fixed",
    top: 0,
    left: 0,
    right: 0,
    background: isDarkMode ? "rgba(15,23,42,.65)" : "rgba(255,255,255,.9)",
    backdropFilter: "blur(16px)",
    WebkitBackdropFilter: "blur(16px)",
    borderBottom: isDarkMode ? "1px solid rgba(255,255,255,.08)" : "1px solid #E2E8F0",
    padding: "1rem 2rem",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    zIndex: 9,
  };
  const leftSectionStyles = { display: "flex", alignItems: "center", gap: "1.2rem" };
  const logoStyles = {
    width: 46,
    height: 46,
    borderRadius: 12,
    objectFit: "cover",
    background: "linear-gradient(135deg,#2563EB,#60A5FA)",
    padding: 2,
  };
  const brandTextStyles = {
    fontSize: "1.6rem",
    fontWeight: 900,
    background: "linear-gradient(135deg,#2563EB,#60A5FA)",
    WebkitBackgroundClip: "text",
    WebkitTextFillColor: "transparent",
  };
  const navLinksStyles = { display: "flex", gap: "2rem", alignItems: "center" };
  const navLinkStyles = { color: isDarkMode ? "#9FB0D0" : "#475569", textDecoration: "none", fontWeight: 600 };
  const activeLinkStyles = { ...navLinkStyles, color: "#2563EB" };

  return (
    <div className={`login-page ${isDarkMode ? "dark" : ""}`}>
      {/* Background layers */}
      <div className="bg-grid" />
      <div className="bg-glows" />
      <div className="bg-stars">
        {Array.from({ length: 22 }).map((_, i) => (
          <span className="star" key={i} />
        ))}
      </div>

      {/* NAV */}
      <nav style={navbarStyles}>
        <div style={leftSectionStyles}>
          <img
            src="/123.png"
            alt="Posting Expert"
            style={logoStyles}
            onError={(e) => {
              e.target.style.display = "none";
            }}
          />
          <span style={brandTextStyles}>Posting Expert</span>
          <button className="theme-toggle" onClick={toggleTheme} title="Toggle theme">
            {isDarkMode ? "‚òÄÔ∏è" : "üåô"}
          </button>
        </div>

        <div style={navLinksStyles}>
          <Link to="/login" style={activeLinkStyles}>
            Home
          </Link>
          <Link to="/about" style={navLinkStyles}>
            About
          </Link>
          <Link to="/privacy-policy" style={navLinkStyles}>
            Privacy Policy
          </Link>
          <Link to="/contact-us" style={navLinkStyles}>
            Contact Us
          </Link>
        </div>

        <div>
          <button onClick={openLoginModal} type="button" className="btn btn-violet">
            Login
          </button>
        </div>
      </nav>

      {/* HERO */}
      <section className="hero-wrap">
        <div className="hero-inner">
          <div className="hero-left">
            <div className="hero-logo">
              <img src="/123.png" alt="Posting Expert" />
            </div>
            <h1 className="hero-title">Posting Expert</h1>
            <p className="hero-sub">AI-Powered Social Media Automation</p>

            <h2 className="hero-typed">
              <span className="typed">{typedText}</span>
              <span className="cursor">|</span>
            </h2>

            <p className="hero-desc">
              Transform your social media strategy with cutting-edge AI technology. Generate stunning content, schedule
              posts, and grow your presence across multiple platforms.
            </p>

            <button className="btn btn-violet" onClick={openLoginModal} type="button">
              Get Started Now <span className="btn-arrow">‚Üí</span>
            </button>
          </div>
        </div>
      </section>

      {/* LOGIN MODAL */}
      <div className={`modal ${showLoginModal ? "show" : ""}`} aria-hidden={!showLoginModal}>
        <div className="modal-card" role="dialog" aria-modal="true" aria-label="Login form">
          <button className="modal-close" onClick={closeLoginModal} title="Close (Esc)" type="button">
            ‚úï
          </button>

          <div className="form-header">
            <h3>Welcome Back</h3>
            <p>Access your AI marketing dashboard</p>
          </div>

          <form className="form" onSubmit={handleSubmit}>
            <label className="label">USERNAME</label>
            <input
              className="input"
              type="text"
              placeholder="Enter username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              autoComplete="off"
              required
            />

            <label className="label">PASSWORD</label>
            <div className="password-row">
              <input
                className="input"
                type={showPassword ? "text" : "password"}
                placeholder="Enter password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                autoComplete="off"
                required
              />
              <button
                type="button"
                className="peek"
                onClick={() => setShowPassword((s) => !s)}
                aria-label="Toggle password visibility"
                title="Show/Hide password"
              >
                {showPassword ? "üëÅÔ∏è" : "üëÅÔ∏è‚Äçüó®Ô∏è"}
              </button>
            </div>

            <label className="remember">
              <input type="checkbox" checked={rememberMe} onChange={(e) => setRememberMe(e.target.checked)} />
              Remember me for 30 days
            </label>

            {error && (
              <div className="alert alert-err">
                <span>‚ö†Ô∏è</span>
                <span>{error}</span>
              </div>
            )}
            {success && (
              <div className="alert alert-ok">
                <span>‚úì</span>
                <span>{success}</span>
              </div>
            )}

            <button className="btn btn-azure" type="submit" disabled={isLoading}>
              {isLoading ? (
                <>
                  <span className="spinner" /> Processing...
                </>
              ) : (
                <>
                  LAUNCH DASHBOARD <span className="btn-arrow">‚Üí</span>
                </>
              )}
            </button>
          </form>

          <div className="form-foot">
            New to Posting Expert? <Link to="/register">Create Account</Link>
          </div>
        </div>
      </div>

      <footer className="site-foot">
        ¬© 2025 Posting Expert. All rights reserved. | Powered by Inikola Technologies & CraftingBrain
      </footer>
    </div>
  );
};

export default Login;
