// Home.js
import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Home.css';

const Home = () => {
  const [showLoginModal, setShowLoginModal] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const [isDarkMode, setIsDarkMode] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const savedTheme = localStorage.getItem('theme');
    if (savedTheme === 'light') {
      setIsDarkMode(false);
      document.body.classList.remove('dark');
      document.body.classList.add('light');
    } else {
      setIsDarkMode(true);
      document.body.classList.add('dark');
      document.body.classList.remove('light');
    }
    
    // Show login modal after a short delay
    const timer = setTimeout(() => {
      setShowLoginModal(true);
    }, 2000);
    
    return () => clearTimeout(timer);
  }, []);

  const toggleTheme = () => {
    const newMode = !isDarkMode;
    setIsDarkMode(newMode);
    localStorage.setItem('theme', newMode ? 'dark' : 'light');
    
    if (newMode) {
      document.body.classList.add('dark');
      document.body.classList.remove('light');
    } else {
      document.body.classList.remove('dark');
      document.body.classList.add('light');
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);

    try {
      const response = await axios.post('http://13.233.45.167:5000/user/login', {
        username,
        password,
      });

      const { token } = response.data;
      const expiryTime = Date.now() + 60 * 60 * 1000;
      
      localStorage.setItem('token', token);
      localStorage.setItem('tokenExpiry', expiryTime);
      localStorage.setItem('username', username);
      
      setShowLoginModal(false);
      navigate('/connect');
    } catch (err) {
      setError(err.response?.data?.error || 'Login failed. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const features = [
    {
      icon: 'ü§ñ',
      title: 'Generate AI Content',
      description: 'Create engaging posts with advanced AI technology'
    },
    {
      icon: 'üöÄ',
      title: 'Post to Multiple Platforms',
      description: 'Schedule content across all social media platforms'
    },
    {
      icon: '‚è∞',
      title: 'Smart Scheduling',
      description: 'Automate posting at optimal times for maximum reach'
    },
    {
      icon: 'üìà',
      title: 'Boost Engagement',
      description: 'Increase interactions and grow your audience'
    },
    {
      icon: '‚ö°',
      title: 'Automate Marketing',
      description: 'Streamline your entire social media marketing workflow'
    },
    {
      icon: 'üé®',
      title: 'AI Image Generation',
      description: 'Create stunning visuals with AI-powered image generation'
    },
    {
      icon: 'üìä',
      title: 'Advanced Analytics',
      description: 'Track performance and insights in real-time'
    },
    {
      icon: 'ü§ù',
      title: 'CRM Integration',
      description: 'Seamlessly integrate with your customer relationship management'
    }
  ];

  return (
    <div className={`home-container ${isDarkMode ? 'dark' : 'light'}`}>
      {/* DNA Animation Background */}
      <div className="dna-animation">
        <div className="dna-strand">
          {[...Array(50)].map((_, i) => (
            <div key={i} className="dna-node">
              <div className="dna-connector"></div>
            </div>
          ))}
        </div>
        <div className="dna-strand dna-strand-2">
          {[...Array(50)].map((_, i) => (
            <div key={i} className="dna-node">
              <div className="dna-connector"></div>
            </div>
          ))}
        </div>
      </div>

      {/* Header */}
      <header className="home-header">
        <div className="header-container">
          <div className="logo-section">
            <img src="/logo.png" alt="PostingExpert" className="header-logo" />
            <span className="header-brand">PostingExpert</span>
          </div>
          
          <nav className="header-nav">
            <a href="#features" className="nav-link">Features</a>
            <a href="#about" className="nav-link">About</a>
            <a href="#pricing" className="nav-link">Pricing</a>
            <button 
              className="login-btn"
              onClick={() => setShowLoginModal(true)}
            >
              Login
            </button>
            <button className="theme-toggle-btn" onClick={toggleTheme}>
              {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
            </button>
          </nav>
        </div>
      </header>

      {/* Hero Section */}
      <section className="hero-section">
        <div className="hero-content">
          <div className="hero-text">
            <h1 className="hero-title">
              Revolutionize Your Social Media with 
              <span className="gradient-text"> AI Power</span>
            </h1>
            <p className="hero-subtitle">
              PostingExpert automates your social media marketing with cutting-edge AI. 
              Generate content, schedule posts, and analyze performance - all in one platform.
            </p>
            <div className="hero-buttons">
              <button 
                className="cta-button primary"
                onClick={() => setShowLoginModal(true)}
              >
                Get Started Free
              </button>
              <button className="cta-button secondary">
                Watch Demo
              </button>
            </div>
            <div className="hero-stats">
              <div className="stat">
                <span className="stat-number">10K+</span>
                <span className="stat-label">Active Users</span>
              </div>
              <div className="stat">
                <span className="stat-number">50K+</span>
                <span className="stat-label">Posts Created</span>
              </div>
              <div className="stat">
                <span className="stat-number">4.9‚òÖ</span>
                <span className="stat-label">Rating</span>
              </div>
            </div>
          </div>
          <div className="hero-visual">
            <div className="floating-cards">
              <div className="card card-1">
                <span className="card-icon">üì±</span>
                <span className="card-text">Auto Posting</span>
              </div>
              <div className="card card-2">
                <span className="card-icon">üé®</span>
                <span className="card-text">AI Images</span>
              </div>
              <div className="card card-3">
                <span className="card-icon">üìä</span>
                <span className="card-text">Analytics</span>
              </div>
              <div className="card card-4">
                <span className="card-icon">‚ö°</span>
                <span className="card-text">Fast & Easy</span>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Features Section */}
      <section id="features" className="features-section">
        <div className="container">
          <h2 className="section-title">Powerful Features</h2>
          <p className="section-subtitle">
            Everything you need to dominate social media marketing
          </p>
          <div className="features-grid">
            {features.map((feature, index) => (
              <div key={index} className="feature-card">
                <div className="feature-icon">{feature.icon}</div>
                <h3 className="feature-title">{feature.title}</h3>
                <p className="feature-description">{feature.description}</p>
              </div>
            ))}
          </div>
        </div>
      </section>

      {/* CTA Section */}
      <section className="cta-section">
        <div className="container">
          <h2 className="cta-title">Ready to Transform Your Social Media?</h2>
          <p className="cta-subtitle">
            Join thousands of marketers using PostingExpert to save time and boost engagement
          </p>
          <button 
            className="cta-button large"
            onClick={() => setShowLoginModal(true)}
          >
            Start Your Free Trial
          </button>
        </div>
      </section>

      {/* Footer */}
      <footer className="home-footer">
        <div className="container">
          <div className="footer-content">
            <div className="footer-brand">
              <img src="/logo.png" alt="PostingExpert" className="footer-logo" />
              <span className="footer-brand-name">PostingExpert</span>
            </div>
            <div className="footer-links">
              <a href="#privacy" className="footer-link">Privacy</a>
              <a href="#terms" className="footer-link">Terms</a>
              <a href="#contact" className="footer-link">Contact</a>
            </div>
          </div>
          <p className="footer-copyright">
            ¬© 2025 PostingExpert by CraftingBrain. All rights reserved.
          </p>
        </div>
      </footer>

      {/* Login Modal */}
      {showLoginModal && (
        <div className="modal-overlay">
          <div className="login-modal">
            <button 
              className="modal-close"
              onClick={() => setShowLoginModal(false)}
            >
              √ó
            </button>
            
            <div className="modal-header">
              <h3>Welcome to PostingExpert</h3>
              <p>Sign in to access your AI-powered social media dashboard</p>
            </div>

            <form className="login-form" onSubmit={handleLogin}>
              <div className="input-group">
                <label>Username</label>
                <input
                  type="text"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter your username"
                  required
                  disabled={loading}
                />
              </div>

              <div className="input-group">
                <label>Password</label>
                <input
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Enter your password"
                  required
                  disabled={loading}
                />
              </div>

              {error && (
                <div className="error-message">
                  {error}
                </div>
              )}

              <button 
                type="submit" 
                className="submit-button"
                disabled={loading}
              >
                {loading ? 'Signing In...' : 'Sign In'}
              </button>

              <div className="modal-footer">
                <p>Don't have an account? <a href="/register">Sign up</a></p>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Home;
