// src/components/Login.js - Clean Template Design
import React, { useState, useEffect } from "react";
import { useNavigate, Link } from "react-router-dom";
import "./Login.css";

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [rememberMe, setRememberMe] = useState(false);
  const [error, setError] = useState("");
  const [success, setSuccess] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const [showLoginModal, setShowLoginModal] = useState(true); // Auto-open modal
  const navigate = useNavigate();

  useEffect(() => {
    const rememberedUsername = localStorage.getItem("rememberedUsername");
    const rememberedRememberMe = localStorage.getItem("rememberMe") === "true";
    
    if (rememberedUsername && rememberedRememberMe) {
      setUsername(rememberedUsername);
      setRememberMe(true);
    }

    const token = localStorage.getItem("token");
    if (token) {
      const tokenExpiry = localStorage.getItem("tokenExpiry");
      if (tokenExpiry && parseInt(tokenExpiry) > Date.now()) {
        navigate("/connect", { replace: true });
      } else {
        localStorage.removeItem("token");
        localStorage.removeItem("tokenExpiry");
      }
    }
  }, [navigate]);

  const openLoginModal = () => {
    setShowLoginModal(true);
    setError("");
    setSuccess("");
  };

  const closeLoginModal = () => {
    setShowLoginModal(false);
    setError("");
    setSuccess("");
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");
    setSuccess("");
    setIsLoading(true);
    
    try {
      const response = await fetch(
        "https://4fqbpp1yya.execute-api.ap-south-1.amazonaws.com/prod/user/login",
        {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({ username, password, rememberMe }),
        }
      );
      
      const data = await response.json();
      
      if (response.ok) {
        const { token, user, rememberMe: rememberMeResponse, expiresIn } = data;
        
        localStorage.setItem("token", token);
        localStorage.setItem("username", user.username);
        
        const expiryTime = Date.now() + (expiresIn * 1000);
        localStorage.setItem("tokenExpiry", expiryTime.toString());
        
        if (rememberMeResponse) {
          localStorage.setItem("rememberedUsername", username);
          localStorage.setItem("rememberMe", "true");
        } else {
          localStorage.removeItem("rememberedUsername");
          localStorage.removeItem("rememberMe");
        }
        
        setSuccess("Login successful! Redirecting...");
        
        if (onLogin) {
          onLogin(user);
        }
        
        setTimeout(() => {
          setIsLoading(false);
          closeLoginModal();
          navigate("/connect", { replace: true });
        }, 1000);
        
      } else {
        const errorMessage = data.error || "Invalid username or password";
        setError(errorMessage);
        setIsLoading(false);
      }
    } catch (err) {
      setError("Network error. Please try again.");
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      {/* NAVBAR */}
      <nav className="navbar">
        <div className="nav-left">
          <img src="/123.png" alt="Posting Expert" className="nav-logo" />
          <span className="nav-brand">Posting Expert</span>
        </div>
        
        <div className="nav-center">
          <Link to="/login" className="nav-link active">Home</Link>
          <Link to="/about" className="nav-link">About</Link>
          <Link to="/privacy-policy" className="nav-link">Privacy Policy</Link>
          <Link to="/contact-us" className="nav-link">Contact Us</Link>
        </div>

        <div className="nav-right">
          <button onClick={openLoginModal} className="nav-button">
            Login
          </button>
        </div>
      </nav>

      {/* LOGIN MODAL */}
      {showLoginModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <button className="modal-close" onClick={closeLoginModal}>‚úï</button>
            
            <div className="modal-header">
              <h2>Welcome Back</h2>
              <p>Sign in to your account</p>
            </div>

            <form className="login-form" onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Username</label>
                <input
                  type="text"
                  placeholder="Enter your username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>

              <div className="form-group">
                <label>Password</label>
                <div className="password-input">
                  <input
                    type={showPassword ? "text" : "password"}
                    placeholder="Enter your password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                  />
                  <button
                    type="button"
                    className="password-toggle"
                    onClick={() => setShowPassword(!showPassword)}
                  >
                    {showPassword ? 'üëÅÔ∏è' : 'üëÅÔ∏è‚Äçüó®Ô∏è'}
                  </button>
                </div>
              </div>

              <div className="form-options">
                <label className="checkbox-label">
                  <input
                    type="checkbox"
                    checked={rememberMe}
                    onChange={(e) => setRememberMe(e.target.checked)}
                  />
                  <span>Remember me</span>
                </label>
              </div>

              {error && (
                <div className="alert alert-error">
                  <span>‚ö†Ô∏è</span>
                  <span>{error}</span>
                </div>
              )}

              {success && (
                <div className="alert alert-success">
                  <span>‚úì</span>
                  <span>{success}</span>
                </div>
              )}

              <button type="submit" className="submit-button" disabled={isLoading}>
                {isLoading ? (
                  <>
                    <span className="spinner"></span>
                    <span>Signing in...</span>
                  </>
                ) : (
                  <span>Sign In</span>
                )}
              </button>
            </form>

            <div className="modal-footer">
              <p>
                Don't have an account?{' '}
                <Link to="/register">Create one</Link>
              </p>
            </div>
          </div>
        </div>
      )}

      {/* PAGE CONTENT */}
      <div className="page-content">
        <div className="hero-section">
          <div className="brand-showcase">
            <div className="brand-tag">Powered by Inikola Technologies</div>
            <div className="brand-display">
              <img src="/123.png" alt="Posting Expert" className="brand-logo-large" />
              <div className="brand-text">
                <h1>Posting Expert</h1>
                <p>AI-Powered Social Media Automation</p>
              </div>
            </div>
          </div>

          <div className="hero-content">
            <h2 className="hero-title">Transform Your Social Media</h2>
            <p className="hero-subtitle">
              Automate your content creation and posting across multiple platforms with advanced AI technology.
            </p>
          </div>

          <div className="features-grid">
            <div className="feature-box">
              <div className="feature-icon">üîó</div>
              <h3>Multi-Platform</h3>
              <p>Connect all your social media accounts in one place</p>
            </div>
            <div className="feature-box">
              <div className="feature-icon">ü§ñ</div>
              <h3>AI-Powered</h3>
              <p>Generate engaging content automatically</p>
            </div>
            <div className="feature-box">
              <div className="feature-icon">üìä</div>
              <h3>Analytics</h3>
              <p>Track performance and engagement metrics</p>
            </div>
          </div>

          <div className="platforms-section">
            <h3>Supported Platforms</h3>
            <div className="platforms-list">
              <div className="platform-item">
                <img src="/images/linkedin.png" alt="LinkedIn" />
                <span>LinkedIn</span>
              </div>
              <div className="platform-item">
                <img src="/images/instagram.jpg" alt="Instagram" />
                <span>Instagram</span>
              </div>
              <div className="platform-item">
                <img src="/images/facebook.png" alt="Facebook" />
                <span>Facebook</span>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* FOOTER */}
      <footer className="footer">
        <div className="footer-content">
          <div className="footer-col">
            <h4>Posting Expert</h4>
            <p>AI-Powered Social Media Automation</p>
          </div>
          <div className="footer-col">
            <h4>Quick Links</h4>
            <Link to="/login">Home</Link>
            <Link to="/about">About</Link>
            <Link to="/privacy-policy">Privacy</Link>
            <Link to="/contact-us">Contact</Link>
          </div>
          <div className="footer-col">
            <h4>Connect</h4>
            <a href="https://linkedin.com" target="_blank" rel="noopener noreferrer">LinkedIn</a>
            <a href="https://instagram.com" target="_blank" rel="noopener noreferrer">Instagram</a>
            <a href="https://facebook.com" target="_blank" rel="noopener noreferrer">Facebook</a>
          </div>
        </div>
        <div className="footer-bottom">
          <p>¬© 2025 Posting Expert. Built by Inikola √ó CraftingBrain</p>
        </div>
      </footer>
    </div>
  );
};

export default Login;
